static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;
  /* Probe log file to capture control flow and state for the reported no-op casts. */
  FILE *log = fopen("/tmp/logs/402.txt", "a");

  (void) cls;               /* Unused. Silent compiler warning. */
  if (log)
    fprintf(log, "probe: executed (void) cls; cls=%p\n", cls);
  (void) version;           /* Unused. Silent compiler warning. */
  if (log)
    fprintf(log, "probe: executed (void) version; version=%p\n", (const void *) version);
  (void) upload_data;       /* Unused. Silent compiler warning. */
  if (log)
    fprintf(log, "probe: executed (void) upload_data; upload_data=%p\n", (const void *) upload_data);
  (void) upload_data_size;  /* Unused. Silent compiler warning. */
  if (log)
    fprintf(log, "probe: executed (void) upload_data_size; upload_data_size=%p\n", (const void *) upload_data_size);

  if (log)
    fprintf(log, "probe: enter ahc_echo method=%s url=%s req_cls=%p\n",
            method ? method : "(null)", url ? url : "(null)", (void *) *req_cls);

  if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
       (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    if (log) fclose(log);
    return MHD_NO;              /* unexpected method */
  }
  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    *req_cls = &aptr;
    if (log)
    {
      fprintf(log, "probe: first call; set req_cls=%p returning MHD_YES\n", (void *) *req_cls);
      fclose(log);
    }
    return MHD_YES;
  }
  *req_cls = NULL;                 /* reset when done */

  /* WARNING: direct usage of url as filename is for example only!
   * NEVER pass received data directly as parameter to file manipulation
   * functions. Always check validity of data before using.
   */
  if (NULL != strstr (url, "../")) /* Very simplified check! */
    fd = -1;                       /* Do not allow usage of parent directories. */
  else
    fd = open (url + 1, O_RDONLY);

  if (log)
    fprintf(log, "probe: open returned fd=%d for url='%s'\n", fd, url ? url : "(null)");

  if (-1 != fd)
  {
    if ( (0 != fstat (fd, &buf)) ||
         (! S_ISREG (buf.st_mode)) )
    {
      /* not a regular file, refuse to serve */
      if (0 != close (fd))
        abort ();
      fd = -1;
      if (log)
        fprintf(log, "probe: file rejected after fstat/mode check; fd set to -1\n");
    }
  }
  if (-1 == fd)
  {
    response = MHD_create_response_from_buffer_static (strlen (PAGE),
                                                       PAGE);
    ret = MHD_queue_response (connection, MHD_HTTP_NOT_FOUND, response);
    MHD_destroy_response (response);
    if (log)
    {
      fprintf(log, "probe: queued NOT_FOUND response ret=%d\n", (int) ret);
      fclose(log);
    }
  }
  else
  {
    response = MHD_create_response_from_fd64 ((uint64_t) buf.st_size, fd);
    if (NULL == response)
    {
      if (0 != close (fd))
        abort ();
      if (log)
        fclose(log);
      return MHD_NO;
    }
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
    MHD_destroy_response (response);
    if (log)
    {
      fprintf(log, "probe: queued OK response ret=%d fd=%d size=%llu\n",
              (int) ret, fd, (unsigned long long) buf.st_size);
      fclose(log);
    }
  }
  return ret;
}
