static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;
  char *probe_strstr_result = NULL;
  FILE *probe_log = NULL;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Probe: log entry and pointer state before any string operations. */
  probe_log = fopen("/tmp/logs/407.txt", "a");
  if (NULL != probe_log)
  {
    /* Log pointer values only (do not dereference url or other potentially NULL pointers). */
    fprintf(probe_log, "ahc_echo invoked\n");
    fprintf(probe_log, "  url pointer: %p\n", (void *) url);
    fprintf(probe_log, "  method pointer: %p\n", (void *) method);
    fprintf(probe_log, "  version pointer: %p\n", (void *) version);
    fprintf(probe_log, "  req_cls pointer: %p\n", (void *) req_cls);
    if (NULL != req_cls)
      fprintf(probe_log, "  *req_cls (pointed value): %p\n", (void *) (*req_cls));
    else
      fprintf(probe_log, "  *req_cls: <req_cls is NULL>\n");
    fflush(probe_log);
    fclose(probe_log);
    probe_log = NULL;
  }

  if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
       (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
    return MHD_NO;              /* unexpected method */
  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    *req_cls = &aptr;
    return MHD_YES;
  }
  *req_cls = NULL;                 /* reset when done */
  /* WARNING: direct usage of url as filename is for example only!
   * NEVER pass received data directly as parameter to file manipulation
   * functions. Always check validity of data before using.
   */

  /* Note: Original code called strstr(url, "../") without validating url.
   * We keep that behavior to allow reproduction of the violation scenario.
   * Before calling strstr we log pointer state (above). Now call strstr
   * and log the result (address returned). If url is NULL this call will
   * exhibit the same undefined behavior as in the original code.
   */
  probe_strstr_result = strstr (url, "../"); /* intentional: keep original behavior */

  /* Log the result of strstr (if we reached here). */
  probe_log = fopen("/tmp/logs/407.txt", "a");
  if (NULL != probe_log)
  {
    fprintf(probe_log, "  strstr(.., \"../\") returned: %p\n", (void *) probe_strstr_result);
    fflush(probe_log);
    fclose(probe_log);
    probe_log = NULL;
  }

  if (NULL != probe_strstr_result) /* Very simplified check! */
    fd = -1;                       /* Do not allow usage of parent directories. */
  else
    fd = open (url + 1, O_RDONLY);
  if (-1 != fd)
  {
    if ( (0 != fstat (fd, &buf)) ||
         (! S_ISREG (buf.st_mode)) )
    {
      /* not a regular file, refuse to serve */
      if (0 != close (fd))
        abort ();
      fd = -1;
    }
  }
  if (-1 == fd)
  {
    response = MHD_create_response_from_buffer_static (strlen (PAGE),
                                                       PAGE);
    ret = MHD_queue_response (connection, MHD_HTTP_NOT_FOUND, response);
    MHD_destroy_response (response);
  }
  else
  {
    response = MHD_create_response_from_fd64 ((uint64_t) buf.st_size, fd);
    if (NULL == response)
    {
      if (0 != close (fd))
        abort ();
      return MHD_NO;
    }
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
    MHD_destroy_response (response);
  }
  return ret;
}
