static void
ahc_log_probe(const char *where,
              const char *method,
              const char *url,
              const char *version,
              void **req_cls,
              int fd)
{
  FILE *f = fopen("/tmp/logs/442.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "[ahc_echo] %s\n", where);
  if (method != NULL)
    fprintf(f, "  method = %s\n", method);
  if (url != NULL)
    fprintf(f, "  url    = %s\n", url);
  fprintf(f, "  version_ptr = %p\n", (const void *) version);
  if (req_cls != NULL)
    fprintf(f, "  req_cls_ptr = %p, *req_cls = %p\n", (void *) req_cls,
            (void *) ((*req_cls) ? *req_cls : NULL));
  if (fd >= 0)
    fprintf(f, "  fd = %d\n", fd);
  fprintf(f, "---\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  FILE *file;
  int fd;
  DIR *dir;
  struct stat buf;
  char emsg[1024];

  /* Probe: log values before discarding unused parameters. This captures
     the pointer value for `version` before the (void) cast to demonstrate
     that the cast is a no-op (it does not modify the pointer). */
  ahc_log_probe("entry-before-void", method, url, version, req_cls, -1);

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Probe: log values after the (void) usage to show the state is
     unchanged by the no-op cast. */
  ahc_log_probe("entry-after-void", method, url, version, req_cls, -1);

  if (0 != strcmp (method, MHD_HTTP_METHOD_GET))
  {
    ahc_log_probe("unexpected-method", method, url, version, req_cls, -1);
    return MHD_NO;              /* unexpected method */
  }
  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    ahc_log_probe("first-call-set-reqcls", method, url, version, req_cls, -1);
    *req_cls = &aptr;
    return MHD_YES;
  }
  *req_cls = NULL;                  /* reset when done */

  file = fopen (&url[1], "rb");
  if (NULL != file)
  {
    fd = fileno (file);
    ahc_log_probe("file-opened", method, url, version, req_cls, fd);
    if (-1 == fd)
    {
      ahc_log_probe("fileno-failed", method, url, version, req_cls, fd);
      (void) fclose (file);
      return MHD_NO;     /* internal error */
    }
    if ( (0 != fstat (fd, &buf)) ||
         (! S_ISREG (buf.st_mode)) )
    {
      /* not a regular file, refuse to serve */
      ahc_log_probe("fstat-or-not-regular", method, url, version, req_cls, fd);
      fclose (file);
      file = NULL;
    }
  }

  if (NULL == file)
  {
    ahc_log_probe("no-file-opendir", method, url, version, req_cls, -1);
    dir = opendir (".");
    if (NULL == dir)
    {
      /* most likely cause: more concurrent requests than
         available file descriptors / 2 */
      snprintf (emsg,
                sizeof (emsg),
                "Failed to open directory `.': %s\n",
                strerror (errno));
      ahc_log_probe("opendir-failed", method, url, version, req_cls, -1);
      response = MHD_create_response_from_buffer (strlen (emsg),
                                                  emsg,
                                                  MHD_RESPMEM_MUST_COPY);
      if (NULL == response)
      {
        ahc_log_probe("response-creation-failed-after-opendir", method, url, version, req_cls, -1);
        return MHD_NO;
      }
      ret = MHD_queue_response (connection,
                                MHD_HTTP_SERVICE_UNAVAILABLE,
                                response);
      ahc_log_probe("queued-response-service-unavailable", method, url, version, req_cls, -1);
      MHD_destroy_response (response);
    }
    else
    {
      response = MHD_create_response_from_callback (MHD_SIZE_UNKNOWN,
                                                    32 * 1024,
                                                    &dir_reader,
                                                    dir,
                                                    &dir_free_callback);
      if (NULL == response)
      {
        closedir (dir);
        ahc_log_probe("response-create-failed-for-dir", method, url, version, req_cls, -1);
        return MHD_NO;
      }
      ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
      ahc_log_probe("queued-response-dir-ok", method, url, version, req_cls, -1);
      MHD_destroy_response (response);
    }
  }
  else
  {
    response = MHD_create_response_from_callback ((size_t) buf.st_size,
                                                  32 * 1024, /* 32k page size */
                                                  &file_reader,
                                                  file,
                                                  &file_free_callback);
    if (NULL == response)
    {
      fclose (file);
      ahc_log_probe("response-create-failed-for-file", method, url, version, req_cls, fd);
      return MHD_NO;
    }
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
    ahc_log_probe("queued-response-file-ok", method, url, version, req_cls, fd);
    MHD_destroy_response (response);
  }

  ahc_log_probe("returning", method, url, version, req_cls, -1);
  return ret;
}
