#include <stdio.h>
#include <stdarg.h>

/* Probe/logging function to capture control flow and state. */
static void ahc_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/506.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  /* Only one user has access to the page */
  static const char *username = "testuser";
  static const char *password = "testpass";
  static const char *realm = "test@example.com";
  enum MHD_DigestAuthResult res_e;
  enum MHD_Result ret;
  static int already_called_marker;

  /* Log entry and parameter/state snapshot before the unused-cast statements */
  ahc_log("ahc_echo ENTER: cls=%p connection=%p url=%p method=%p version=%p upload_data=%p upload_data_size=%p req_cls=%p *req_cls=%p already_marker_addr=%p",
          cls, connection, (const void *)url, (const void *)method, (const void *)version,
          (const void *)upload_data, (const void *)upload_data_size, (const void *)req_cls,
          (const void *)(req_cls ? *req_cls : NULL), (const void *)&already_called_marker);

  ahc_log("ahc_echo: about to execute (void) casts for unused parameters (these casts are executed but have no runtime effect)");

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) method;            /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log after the (void) casts to show that executing them did not change state */
  ahc_log("ahc_echo: after (void) casts: req_cls=%p *req_cls=%p already_marker_addr=%p",
          (const void *)req_cls, (const void *)(req_cls ? *req_cls : NULL), (const void *)&already_called_marker);

  if (&already_called_marker != *req_cls)
  { /* Called for the first time, request not fully read yet */
    ahc_log("ahc_echo: first call detected: setting *req_cls to marker (%p) and returning MHD_YES", (const void *)&already_called_marker);

    *req_cls = &already_called_marker;
    /* Wait for complete request */
    return MHD_YES;
  }

  ahc_log("ahc_echo: continuing after first-call marker, calling MHD_digest_auth_check3");

  /* No need to call MHD_digest_auth_get_username3() as the only
   * one user has an access. The username match is checked by
   * MHD_digest_auth_check3() function. */
  res_e = MHD_digest_auth_check3 (
    connection,
    realm,
    username,
    password,
    0, 0,
    MHD_DIGEST_AUTH_MULT_QOP_ANY_NON_INT,
    MHD_DIGEST_AUTH_MULT_ALGO3_ANY_NON_SESSION);

  ahc_log("ahc_echo: MHD_digest_auth_check3 returned res_e=%d", (int)res_e);

  if (res_e != MHD_DAUTH_OK)
  {
    ahc_log("ahc_echo: authentication failed (res_e=%d), preparing auth-required response", (int)res_e);

    response =
      MHD_create_response_from_buffer_static (strlen (DENIED),
                                              DENIED);
    if (NULL == response)
    {
      ahc_log("ahc_echo: MHD_create_response_from_buffer_static returned NULL -> returning MHD_NO");
      return MHD_NO;
    }
    ret = MHD_queue_auth_required_response3 (
      connection,
      realm,
      MY_OPAQUE_STR,
      NULL,
      response,
      (res_e == MHD_DAUTH_NONCE_STALE) ? MHD_YES : MHD_NO,
      MHD_DIGEST_AUTH_MULT_QOP_ANY_NON_INT,
      MHD_DIGEST_AUTH_MULT_ALGO3_ANY_NON_SESSION,
      MHD_NO,
      MHD_YES);

    ahc_log("ahc_echo: queued auth-required response, ret=%d", (int)ret);

    MHD_destroy_response (response);
    ahc_log("ahc_echo: destroyed response after auth-required flow, returning ret=%d", (int)ret);
    return ret;
  }

  ahc_log("ahc_echo: authentication OK, creating normal PAGE response");
  response = MHD_create_response_from_buffer_static (strlen (PAGE), PAGE);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  ahc_log("ahc_echo: queued normal response, ret=%d", (int)ret);
  MHD_destroy_response (response);
  ahc_log("ahc_echo: destroyed response, returning ret=%d", (int)ret);
  return ret;
}
