#include <stdio.h>
#include <stdarg.h>

/* Probe logger: append human-readable traces to /tmp/logs/506.txt */
static void ahc_echo_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/506.txt", "a");
  if (f == NULL)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  /* Only one user has access to the page */
  static const char *username = "testuser";
  static const char *password = "testpass";
  static const char *realm = "test@example.com";
  enum MHD_DigestAuthResult res_e;
  enum MHD_Result ret;
  static int already_called_marker;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) method;            /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log entry and parameter pointers. These logs help demonstrate that
   * the (void) upload_data; cast is a no-op: upload_data is not used by
   * the function beyond being logged here (no side-effects). */
  ahc_echo_log("ahc_echo: entry cls=%p connection=%p url=%p method=%p version=%p upload_data=%p upload_data_size=%p req_cls=%p",
               cls, (void *) connection, (const void *) url, (const void *) method, (const void *) version,
               (const void *) upload_data, (const void *) upload_data_size, (void *) req_cls);

  ahc_echo_log("ahc_echo: executed unused casts including (void) upload_data; upload_data pointer=%p",
               (const void *) upload_data);

  /* For safer logging we capture current *req_cls if req_cls is non-NULL.
   * Note: original logic dereferences *req_cls below without a NULL check
   * (it is expected MHD provides a valid pointer). We only read it for
   * logging to show the control-flow decision that follows. */
  void *req_cls_val = NULL;
  if (req_cls)
    req_cls_val = *req_cls;
  ahc_echo_log("ahc_echo: before first-call check req_cls_ptr=%p req_cls_val=%p marker_addr=%p",
               (void *) req_cls, req_cls_val, (void *) &already_called_marker);

  if (&already_called_marker != *req_cls)
  { /* Called for the first time, request not fully read yet */
    ahc_echo_log("ahc_echo: first-time entry: setting *req_cls to marker (%p)", (void *) &already_called_marker);
    *req_cls = &already_called_marker;
    /* Wait for complete request */
    ahc_echo_log("ahc_echo: returning MHD_YES to wait for more data");
    return MHD_YES;
  }

  /* No need to call MHD_digest_auth_get_username3() as the only
   * one user has an access. The username match is checked by
   * MHD_digest_auth_check3() function. */
  res_e = MHD_digest_auth_check3 (
    connection,
    realm,
    username,
    password,
    0, 0,
    MHD_DIGEST_AUTH_MULT_QOP_ANY_NON_INT,
    MHD_DIGEST_AUTH_MULT_ALGO3_ANY_NON_SESSION);
  ahc_echo_log("ahc_echo: digest auth result res_e=%d", (int) res_e);

  if (res_e != MHD_DAUTH_OK)
  {
    ahc_echo_log("ahc_echo: auth failed (res_e=%d); creating auth-required response", (int) res_e);
    response =
      MHD_create_response_from_buffer_static (strlen (DENIED),
                                              DENIED);
    if (NULL == response)
    {
      ahc_echo_log("ahc_echo: failed to create response; returning MHD_NO");
      return MHD_NO;
    }
    ret = MHD_queue_auth_required_response3 (
      connection,
      realm,
      MY_OPAQUE_STR,
      NULL,
      response,
      (res_e == MHD_DAUTH_NONCE_STALE) ? MHD_YES : MHD_NO,
      MHD_DIGEST_AUTH_MULT_QOP_ANY_NON_INT,
      MHD_DIGEST_AUTH_MULT_ALGO3_ANY_NON_SESSION,
      MHD_NO,
      MHD_YES);

    ahc_echo_log("ahc_echo: queued auth-required response; ret=%d", (int) ret);

    MHD_destroy_response (response);
    return ret;
  }
  response = MHD_create_response_from_buffer_static (strlen (PAGE), PAGE);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  ahc_echo_log("ahc_echo: success path queued response; ret=%d", (int) ret);
  MHD_destroy_response (response);
  ahc_echo_log("ahc_echo: exiting with ret=%d", (int) ret);
  return ret;
}
