#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logger: append human-readable traces to /tmp/logs/530.txt */
static void ahc_echo_logf(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/530.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct MHD_Response *response;
  enum MHD_Result ret;
  struct MHD_BasicAuthInfo *auth_info;
  int fail;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log: function entry and explicit note that the '(void) version;' cast was executed. */
  ahc_echo_logf("ahc_echo: called; executed '(void) version;' (version intentionally not dereferenced); method='%s'", (method != NULL) ? method : "(null)");

  if (0 != strcmp (method, "GET"))
  {
    ahc_echo_logf("ahc_echo: unexpected method '%s' -> returning MHD_NO", method);
    return MHD_NO;              /* unexpected method */
  }

  if (&aptr != *req_cls)
  {
    /* do never respond on first call */
    *req_cls = &aptr;
    ahc_echo_logf("ahc_echo: first invocation for this connection; setting req_cls to %p and returning MHD_YES", (void *) &aptr);
    return MHD_YES;
  }
  *req_cls = NULL;                  /* reset when done */
  ahc_echo_logf("ahc_echo: continuing after initial call; req_cls reset to NULL");

  /* require: "Aladdin" with password "open sesame" */
  auth_info = MHD_basic_auth_get_username_password3 (connection);
  ahc_echo_logf("ahc_echo: MHD_basic_auth_get_username_password3 returned %p", (void *) auth_info);

  fail = ( (NULL == auth_info) ||
           (strlen ("Aladdin") != auth_info->username_len) ||
           (0 != memcmp (auth_info->username, "Aladdin",
                         auth_info->username_len)) ||
           /* The next check against NULL is optional,
            * if 'password' is NULL then 'password_len' is always zero. */
           (NULL == auth_info->password) ||
           (strlen ("open sesame") != auth_info->password_len) ||
           (0 != memcmp (auth_info->password, "open sesame",
                         auth_info->password_len)) );

  /* Log details useful to reproduce the auth behavior without changing program logic.
     Avoid dereferencing auth_info unless non-NULL. */
  if (NULL != auth_info)
  {
    ahc_echo_logf("ahc_echo: auth_info present: username_len=%zu, password_len=%zu", (size_t) auth_info->username_len, (size_t) auth_info->password_len);
  }
  else
  {
    ahc_echo_logf("ahc_echo: auth_info is NULL");
  }

  if (fail)
  {
    ahc_echo_logf("ahc_echo: authentication FAILED -> queuing basic auth required response");
    response =
      MHD_create_response_from_buffer_static (strlen (DENIED),
                                              (const void *) DENIED);
    ret = MHD_queue_basic_auth_required_response3 (connection,
                                                   "TestRealm",
                                                   MHD_NO,
                                                   response);
  }
  else
  {
    ahc_echo_logf("ahc_echo: authentication SUCCEEDED -> queuing PAGE response");
    response =
      MHD_create_response_from_buffer_static (strlen (PAGE),
                                              (const void *) PAGE);
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  }

  if (NULL != auth_info)
  {
    MHD_free (auth_info);
    ahc_echo_logf("ahc_echo: freed auth_info");
  }
  MHD_destroy_response (response);
  ahc_echo_logf("ahc_echo: destroyed response; returning ret=%d", (int) ret);
  return ret;
}
