#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>

/* Probe logging function to record control flow and state. */
static void ahc_echo_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/538.txt", "a");
  if (NULL == f)
    return; /* best-effort logging; do not alter control flow if logging fails */

  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct handler_param *param = (struct handler_param *) cls;
  struct MHD_Response *response;
  enum MHD_Result ret;

  /* Original unused-parameter casts are preserved. We log immediately after
     to provide evidence they were executed at runtime. */
  (void) cls;               /* Unused. Silent compiler warning. */
  ahc_echo_log("ahc_echo: executed (void) cls; cls=%p", cls);
  (void) url;               /* Unused. Silent compiler warning. */
  ahc_echo_log("ahc_echo: executed (void) url; url=%p", url);
  (void) version;           /* Unused. Silent compiler warning. */
  ahc_echo_log("ahc_echo: executed (void) version; version=%p", version);
  (void) upload_data;       /* Unused. Silent compiler warning. */
  ahc_echo_log("ahc_echo: executed (void) upload_data; upload_data=%p", (const void *) upload_data);
  (void) upload_data_size;  /* Unused. Silent compiler warning. */
  ahc_echo_log("ahc_echo: executed (void) upload_data_size; upload_data_size=%p", (const void *) upload_data_size);

  ahc_echo_log("ahc_echo entry: connection=%p url_str=%s method=%s version_str=%s upload_data=%p upload_data_size_ptr=%p req_cls=%p *req_cls=%p",
               connection,
               (url ? url : "(null)"),
               (method ? method : "(null)"),
               (version ? version : "(null)"),
               (const void *) upload_data,
               (const void *) upload_data_size,
               (const void *) req_cls,
               (const void *) (req_cls ? *req_cls : NULL));

  if ((0 != strcmp (method, "GET")) && (0 != strcmp (method, "POST")))
  {
    ahc_echo_log("ahc_echo: unexpected method '%s' -> returning MHD_NO", method);
    return MHD_NO;              /* unexpected method */
  }

  if (&aptr != *req_cls)
  {
    ahc_echo_log("ahc_echo: initial request context mismatch: &aptr=%p *req_cls=%p -> setting *req_cls=&aptr",
                 (void *) &aptr, (void *) *req_cls);

    *req_cls = &aptr;

    /* always too busy for POST requests */
    if (0 == strcmp (method, "POST"))
    {
      ahc_echo_log("ahc_echo: POST request while busy; preparing BUSYPAGE response");
      response =
        MHD_create_response_from_buffer_static (strlen (BUSYPAGE),
                                                (const void *) BUSYPAGE);
      ret =
        MHD_queue_response (connection, MHD_HTTP_SERVICE_UNAVAILABLE,
                            response);
      ahc_echo_log("ahc_echo: queued BUSYPAGE response, ret=%d", (int) ret);
      MHD_destroy_response (response);
      ahc_echo_log("ahc_echo: destroyed BUSYPAGE response; returning ret=%d", (int) ret);
      return ret;
    }
  }

  *req_cls = NULL;                  /* reset when done */
  ahc_echo_log("ahc_echo: resetting *req_cls to NULL; preparing normal response; param=%p param->response_page=%p",
               (void *) param,
               (param ? (void *) param->response_page : NULL));

  response =
    MHD_create_response_from_buffer_static (strlen (param->response_page),
                                            (const void *)
                                            param->response_page);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  ahc_echo_log("ahc_echo: queued normal response, ret=%d", (int) ret);
  MHD_destroy_response (response);
  ahc_echo_log("ahc_echo: destroyed normal response; returning ret=%d", (int) ret);
  return ret;
}
