#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logger: append logs to /tmp/logs/539.txt */
static void ahc_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/539.txt", "a");
  if (NULL == f)
  {
    return;
  }
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  static int aptr;
  struct handler_param *param = (struct handler_param *) cls;
  struct MHD_Response *response;
  enum MHD_Result ret;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log entry and key pointer state */
  ahc_probe_log("ahc_echo ENTRY: method=\"%s\", url=\"%s\", version=\"%s\"",
                method ? method : "(null)", url ? url : "(null)", version ? version : "(null)");
  ahc_probe_log("ahc_echo POINTER STATE: req_cls=%p, *req_cls=%p, &aptr=%p, aptr_value=%d",
                (void *) &req_cls,
                (void *) (req_cls ? *req_cls : NULL),
                (void *) &aptr,
                aptr);

  if ((0 != strcmp (method, "GET")) && (0 != strcmp (method, "POST")))
  {
    ahc_probe_log("ahc_echo: unexpected method '%s' -> returning MHD_NO", method);
    return MHD_NO;              /* unexpected method */
  }

  if (&aptr != *req_cls)
  {
    ahc_probe_log("ahc_echo: &aptr (%p) != *req_cls (%p) -> setting *req_cls = &aptr",
                  (void *) &aptr, (void *) *req_cls);

    *req_cls = &aptr;

    ahc_probe_log("ahc_echo: stored &aptr (%p) into *req_cls -> now *req_cls=%p",
                  (void *) &aptr, (void *) *req_cls);

    /* always to busy for POST requests */
    if (0 == strcmp (method, "POST"))
    {
      ahc_probe_log("ahc_echo: method is POST, returning BUSY page");
      response =
        MHD_create_response_from_buffer_static (strlen (BUSYPAGE),
                                                (const void *) BUSYPAGE);
      ret =
        MHD_queue_response (connection, MHD_HTTP_SERVICE_UNAVAILABLE,
                            response);
      ahc_probe_log("ahc_echo: queued response (BUSY), ret=%d", (int) ret);
      MHD_destroy_response (response);
      ahc_probe_log("ahc_echo: exiting (POST busy)");
      return ret;
    }
  }
  else
  {
    ahc_probe_log("ahc_echo: &aptr (%p) == *req_cls (%p) -> continuing without changing *req_cls",
                  (void *) &aptr, (void *) *req_cls);
  }

  ahc_probe_log("ahc_echo: resetting *req_cls from %p to NULL", (void *) *req_cls);
  *req_cls = NULL;                  /* reset when done */
  response =
    MHD_create_response_from_buffer_static (strlen (param->response_page),
                                            (const void *)
                                            param->response_page);
  ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  ahc_probe_log("ahc_echo: queued OK response, ret=%d", (int) ret);
  MHD_destroy_response (response);
  ahc_probe_log("ahc_echo: EXIT returning ret=%d", (int) ret);
  return ret;
}
