#include <stdio.h>
#include <stdlib.h>
#include <stdint.h>
#include <inttypes.h>
#include <string.h>
#include <errno.h>

/* Probe helper: append a compact log entry describing control flow and key pointers.
   Logs are written to /tmp/logs/602.txt so other agents can inspect execution.
   This helper intentionally avoids dereferencing application-specific structures
   to minimize coupling and avoid changing program behaviour. */
static void ahc_echo_probe(const char *tag,
                           const void *cls,
                           const void *connection,
                           const char *url,
                           const char *method,
                           const char *version,
                           const char *upload_data,
                           const void *upload_data_size_ptr,
                           const void *req_cls_ptr,
                           const void *req_ptr)
{
  FILE *fp = fopen("/tmp/logs/602.txt", "a");
  if (NULL == fp)
  {
    /* Logging failure should not affect program behaviour. */
    return;
  }
  /* Print tag and pointer values. Avoid dereferencing unknown structures. */
  fprintf(fp, "%s: cls=%p connection=%p url=%p method=%p version=%p upload_data=%p upload_data_size=%p req_cls=%p req=%p\n",
          tag,
          cls,
          connection,
          (const void *) url,
          (const void *) method,
          (const void *) version,
          (const void *) upload_data,
          upload_data_size_ptr,
          req_cls_ptr,
          req_ptr);
  /* If URL pointer is non-NULL, write a short prefix to help identify the request. */
  if (NULL != url)
  {
    /* Write up to 256 bytes of URL to the log to avoid huge lines. */
    size_t len = strnlen(url, 256);
    if (len > 0)
    {
      fprintf(fp, "%s: url_text=\"");
      fwrite(url, 1, len, fp);
      fprintf(fp, "\"\n");
    }
  }
  fclose(fp);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  struct Request *req;
  struct itimerspec ts;

  /* Original cast to silence unused-parameter warning. This statement is
     side-effect-free; the probe below documents that this path was executed. */
  (void) cls;
  (void) method;
  (void) version;           /* Unused. Silence compiler warning. */
  (void) upload_data;       /* Unused. Silence compiler warning. */
  (void) upload_data_size;  /* Unused. Silence compiler warning. */

  /* Log that we reached the start of ahc_echo and that the (void) cls cast
     was executed (by proximity). This provides evidence that the cast
     executes but has no observable effect on program state. */
  ahc_echo_probe("start_after_voids", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, NULL);

  req = *req_cls;

  /* Log after dereferencing req_cls (the stored per-connection pointer). */
  ahc_echo_probe("after_req_assign", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

  if (NULL == req)
  {
    ahc_echo_probe("req_is_null_before_alloc", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

    req = malloc (sizeof(struct Request));
    if (NULL == req)
      return MHD_NO;

    req->connection = connection;
    req->timerfd = -1;
    *req_cls = req;

    ahc_echo_probe("req_initialized_and_returning", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

    return MHD_YES;
  }

  ahc_echo_probe("have_existing_req", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

  if (-1 != req->timerfd)
  {
    /* send response (echo request url) */
    response = MHD_create_response_from_buffer_copy (strlen (url),
                                                     (const void *) url);
    if (NULL == response)
      return MHD_NO;

    ahc_echo_probe("about_to_queue_response", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);

    ahc_echo_probe("response_queued_and_destroyed", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

    return ret;
  }

  /* create timer and suspend connection */
  req->timerfd = timerfd_create (CLOCK_MONOTONIC, TFD_NONBLOCK);
  if (-1 == req->timerfd)
  {
    printf ("timerfd_create: %s", strerror (errno));
    ahc_echo_probe("timerfd_create_failed", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);
    return MHD_NO;
  }

  ahc_echo_probe("timerfd_created", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

  evt.events = EPOLLIN;
  evt.data.ptr = req;
  if (-1 == epoll_ctl (epfd, EPOLL_CTL_ADD, req->timerfd, &evt))
  {
    printf ("epoll_ctl: %s", strerror (errno));
    ahc_echo_probe("epoll_ctl_failed", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);
    return MHD_NO;
  }

  ahc_echo_probe("epoll_ctl_added", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

  ts.it_value.tv_sec = 1;
  ts.it_value.tv_nsec = 0;
  ts.it_interval.tv_sec = 0;
  ts.it_interval.tv_nsec = 0;
  if (-1 == timerfd_settime (req->timerfd, 0, &ts, NULL))
  {
    printf ("timerfd_settime: %s", strerror (errno));
    ahc_echo_probe("timerfd_settime_failed", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);
    return MHD_NO;
  }

  ahc_echo_probe("timerfd_settime_ok", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

  MHD_suspend_connection (connection);

  ahc_echo_probe("connection_suspended_returning_yes", cls, connection, url, method, version, upload_data, upload_data_size, req_cls, req);

  return MHD_YES;
}
