static void
ahc_echo_log_state(const char *label,
                   const char *url,
                   const char *method,
                   const char *version,
                   const char *upload_data, size_t *upload_data_size,
                   void **req_cls, struct Request *req,
                   struct MHD_Connection *connection)
{
  FILE *f = fopen("/tmp/logs/602.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] ahc_echo_log_state: %s\n", (long)t, label ? label : "(null)");
  fprintf(f, "  url=%s\n", url ? url : "(null)");
  fprintf(f, "  method=%s\n", method ? method : "(null)");
  fprintf(f, "  version=%s\n", version ? version : "(null)");
  fprintf(f, "  upload_data_ptr=%p present=%s\n", (void *) upload_data, upload_data ? "yes" : "no");
  if (upload_data && upload_data[0])
    fprintf(f, "  upload_data(leading)='%.64s'\n", upload_data);
  fprintf(f, "  upload_data_size_ptr=%p\n", (void *) upload_data_size);
  if (upload_data_size)
    fprintf(f, "  *upload_data_size=%zu\n", *upload_data_size);
  fprintf(f, "  req_cls_ptr=%p req_ptr=%p connection_ptr=%p\n",
          (void *) req_cls, (void *) req, (void *) connection);
  fflush(f);
  fclose(f);
}

static enum MHD_Result
ahc_echo (void *cls,
          struct MHD_Connection *connection,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data, size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  struct Request *req;
  struct itimerspec ts;

  (void) cls;
  (void) method;
  (void) version;           /* Unused. Silence compiler warning. */
  (void) upload_data;       /* Unused. Silence compiler warning. */
  (void) upload_data_size;  /* Unused. Silence compiler warning. */

  /* Probe: record that the no-op void casts (including the (void) upload_data)
     were executed. This provides evidence that the statement exists and is
     reached at runtime (it executes as a no-op). */
  ahc_echo_log_state("after_void_casts", url, method, version,
                     upload_data, upload_data_size, req_cls, NULL, connection);

  req = *req_cls;

  /* Probe: record state after dereferencing req_cls. */
  ahc_echo_log_state("after_req_cls_deref", url, method, version,
                     upload_data, upload_data_size, req_cls, req, connection);

  if (NULL == req)
  {
    ahc_echo_log_state("allocating_req_start", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);

    req = malloc (sizeof(struct Request));
    if (NULL == req)
    {
      ahc_echo_log_state("allocating_req_failed", url, method, version,
                         upload_data, upload_data_size, req_cls, req, connection);
      return MHD_NO;
    }

    req->connection = connection;
    req->timerfd = -1;
    *req_cls = req;

    ahc_echo_log_state("allocating_req_success", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);

    return MHD_YES;
  }

  if (-1 != req->timerfd)
  {
    /* Probe: timerfd already set, will send response (echo request url) */
    ahc_echo_log_state("sending_response_path", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);

    response = MHD_create_response_from_buffer_copy (strlen (url),
                                                     (const void *) url);
    if (NULL == response)
    {
      ahc_echo_log_state("response_creation_failed", url, method, version,
                         upload_data, upload_data_size, req_cls, req, connection);
      return MHD_NO;
    }
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);

    ahc_echo_log_state("response_queued", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);

    return ret;
  }

  /* create timer and suspend connection */
  ahc_echo_log_state("creating_timer", url, method, version,
                     upload_data, upload_data_size, req_cls, req, connection);

  req->timerfd = timerfd_create (CLOCK_MONOTONIC, TFD_NONBLOCK);
  if (-1 == req->timerfd)
  {
    printf ("timerfd_create: %s", strerror (errno));
    ahc_echo_log_state("timerfd_create_failed", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);
    return MHD_NO;
  }
  evt.events = EPOLLIN;
  evt.data.ptr = req;
  if (-1 == epoll_ctl (epfd, EPOLL_CTL_ADD, req->timerfd, &evt))
  {
    printf ("epoll_ctl: %s", strerror (errno));
    ahc_echo_log_state("epoll_ctl_failed", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);
    return MHD_NO;
  }
  ts.it_value.tv_sec = 1;
  ts.it_value.tv_nsec = 0;
  ts.it_interval.tv_sec = 0;
  ts.it_interval.tv_nsec = 0;
  if (-1 == timerfd_settime (req->timerfd, 0, &ts, NULL))
  {
    printf ("timerfd_settime: %s", strerror (errno));
    ahc_echo_log_state("timerfd_settime_failed", url, method, version,
                       upload_data, upload_data_size, req_cls, req, connection);
    return MHD_NO;
  }

  MHD_suspend_connection (connection);

  ahc_echo_log_state("suspended_connection", url, method, version,
                     upload_data, upload_data_size, req_cls, req, connection);

  return MHD_YES;
}
