static void
probe_log(const char *event, int sys_cpu_count, int proc_cpu_count)
{
    FILE *f = fopen("/tmp/logs/324.txt", "a");
    if (f)
    {
        fprintf(f, "EVENT=%s, sys_cpu_count=%d, proc_cpu_count=%d\n",
                event, sys_cpu_count, proc_cpu_count);
        fclose(f);
    }
}

static unsigned int
detect_cpu_core_count (void)
{
  int sys_cpu_count;
  int proc_cpu_count = -1; /* -1 means unknown */

  sys_cpu_count = mhd_tool_get_system_cpu_count ();
  probe_log("after_get_system_cpu_count", sys_cpu_count, proc_cpu_count);

  if (0 >= sys_cpu_count)
  {
    fprintf (stderr, "Failed to detect the number of logical CPU cores "
             "available on the system.\n");
    probe_log("enter_fallback_path", sys_cpu_count, proc_cpu_count);

    proc_cpu_count = mhd_tool_get_proc_cpu_count ();
    probe_log("after_get_proc_cpu_count", sys_cpu_count, proc_cpu_count);

    if (0 < proc_cpu_count)
    {
      fprintf (stderr, "The number of CPU cores available for this process "
               "is used as a fallback.\n");
      probe_log("using_proc_cpu_count_fallback", sys_cpu_count, proc_cpu_count);
      sys_cpu_count = proc_cpu_count;
    }
#ifdef MHD_REAL_CPU_COUNT
    if (0 >= sys_cpu_count)
    {
      fprintf (stderr, "configure-detected hardcoded number is used "
               "as a fallback.\n");
      probe_log("before_hardcoded_fallback", sys_cpu_count, proc_cpu_count);
      sys_cpu_count = MHD_REAL_CPU_COUNT;
      probe_log("after_hardcoded_fallback", sys_cpu_count, proc_cpu_count);
    }
#endif
    if (0 >= sys_cpu_count)
      sys_cpu_count = 1;
    probe_log("final_fallback_value", sys_cpu_count, proc_cpu_count);

    printf ("Assuming %d logical CPU core%s on this system.\n", sys_cpu_count,
            (1 == sys_cpu_count) ? "" : "s");
  }
  else
  {
    probe_log("detected_system_value", sys_cpu_count, proc_cpu_count);
    printf ("Detected %d logical CPU core%s on this system.\n", sys_cpu_count,
            (1 == sys_cpu_count) ? "" : "s");
  }
  probe_log("about_to_return", sys_cpu_count, proc_cpu_count);
  return (unsigned int) sys_cpu_count;
}
