#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <string.h>
#include <stddef.h>

/* Probe helper: append a formatted message to the log file. */
static void probe_log(const char *fmt, ...)
{
  FILE *fp = fopen("/tmp/logs/466.txt", "a");
  if (NULL == fp)
    return;

  /* timestamp for easier traceability */
  {
    time_t t = time(NULL);
    if (t != (time_t)(-1))
    {
      struct tm tm_buf;
      if (localtime_r(&t, &tm_buf) != NULL)
      {
        char timestr[32];
        if (strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf) > 0)
          fprintf(fp, "%s ", timestr);
      }
    }
  }

  va_list ap;
  va_start(ap, fmt);
  vfprintf(fp, fmt, ap);
  va_end(ap);
  fprintf(fp, "\n");
  fclose(fp);
}

static const char *probe_safe_str(const char *s)
{
  return (NULL != s) ? s : "(null)";
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;

  /* Log entry with contextual parameters for reproducing the no-op discard scenario. */
  probe_log("enter generate_page: cls=%p connection=%p url=%s method=%s version=%s upload_data=%p upload_data_size=%p req_cls=%p",
            cls, (void *) connection, probe_safe_str(url), probe_safe_str(method), probe_safe_str(version), (void *) upload_data, (void *) upload_data_size, (void *) req_cls);

  (void) cls;               /* Unused. Silent compiler warning. */
  /* Evidence log: the explicit discard of 'cls' was executed. */
  probe_log("executed discard: (void) cls; — cls pointer value=%p", cls);

  (void) version;           /* Unused. Silent compiler warning. */
  /* Evidence log: the explicit discard of 'version' was executed. */
  probe_log("executed discard: (void) version; — version string='%s'", probe_safe_str(version));

  if (0 != strcmp (url, "/"))
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
         (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
    {
      probe_log("unexpected method for file download: method=%s", probe_safe_str(method));
      return MHD_NO;    /* unexpected method (we're not polite...) */
    }

    fd = -1;
    if ( (NULL == strstr (&url[1], "..")) &&
         ('/' != url[1]) )
    {
      fd = open (&url[1], O_RDONLY);
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        fd = -1;
      }
    }
    if (-1 == fd)
    {
      probe_log("file not found for url=%s", probe_safe_str(url));
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
    }
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = strrchr (&url[1], '.');

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      probe_log("MHD_create_response_from_fd failed for fd=%d, url=%s", fd, probe_safe_str(url));
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
    {
      probe_log("adding content-type header: %s for url=%s", mime, probe_safe_str(url));
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    }
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);
    probe_log("queued file response for url=%s ret=%d", probe_safe_str(url), (int) ret);
    return ret;
  }

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
        return MHD_NO; /* out of memory, close connection */
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        return MHD_NO;
      }
      *req_cls = uc;
      probe_log("initialized UploadContext for connection=%p req_cls=%p", (void *) connection, (void *) req_cls);
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      probe_log("processing upload chunk for connection=%p, remaining_size set to 0", (void *) connection);
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
    }
    if (NULL != uc->response)
    {
      probe_log("upload finished with response for connection=%p", (void *) connection);
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      update_directory ();
      probe_log("upload finished successfully, updated directory for connection=%p", (void *) connection);
      return return_directory_response (connection);
    }
  }
  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    probe_log("directory listing requested for connection=%p method=%s", (void *) connection, probe_safe_str(method));
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  probe_log("unexpected request: method=%s url=%s", probe_safe_str(method), probe_safe_str(url));
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
