#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <unistd.h>
#include <fcntl.h>

/* Probe function: append a log line describing current pointers/state.
   NOTE: This function intentionally does NOT dereference pointers to C-strings
   (like 'method' or 'url') to avoid introducing new undefined behavior.
   It only logs pointer values and numeric sizes so other agents can reproduce
   the original behavior that leads to the strcmp() UB when 'method' is NULL
   or points to a non-NUL-terminated area. */
static void
log_probe(const char *event,
          const char *url,
          const char *method,
          const char *version,
          const char *upload_data,
          size_t *upload_data_size,
          void **req_cls)
{
  FILE *f = fopen("/tmp/logs/471.txt", "a");
  if (NULL == f)
    return;

  /* Print event and pointer values. Do not attempt to read memory pointed
     to by these pointers (no %s on url/method/version/upload_data). */
  fprintf(f, "EVENT: %s\n", event);
  fprintf(f, "  url=%p, method=%p, version=%p\n",
          (const void *) url,
          (const void *) method,
          (const void *) version);
  fprintf(f, "  upload_data=%p, upload_data_size_ptr=%p",
          (const void *) upload_data,
          (void *) upload_data_size);
  if (NULL != upload_data_size)
    fprintf(f, ", *upload_data_size=%zu\n", *upload_data_size);
  else
    fprintf(f, "\n");
  fprintf(f, "  req_cls_ptr=%p", (void *) req_cls);
  if (NULL != req_cls)
    fprintf(f, ", *req_cls=%p\n", *req_cls);
  else
    fprintf(f, "\n");

  fprintf(f, "----\n");
  fclose(f);
}

static enum MHD_Result
generate_page (void *cls,
               struct MHD_Connection *connection,
               const char *url,
               const char *method,
               const char *version,
               const char *upload_data,
               size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *response;
  enum MHD_Result ret;
  int fd;
  struct stat buf;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  /* Log entry and pointers before first strcmp on 'url'. */
  log_probe("enter_generate_page_before_url_cmp", url, method, version,
            upload_data, upload_data_size, req_cls);

  if (0 != strcmp (url, "/"))
  {
    /* should be file download */
#ifdef MHD_HAVE_LIBMAGIC
    char file_data[MAGIC_HEADER_SIZE];
    ssize_t got;
#endif /* MHD_HAVE_LIBMAGIC */
    const char *mime;

    /* Log before comparing method vs GET/HEAD. This helps capture the
       state when 'method' might be NULL (the reported violation scenario). */
    log_probe("before_method_cmp_get_head", url, method, version,
              upload_data, upload_data_size, req_cls);

    if ( (0 != strcmp (method, MHD_HTTP_METHOD_GET)) &&
         (0 != strcmp (method, MHD_HTTP_METHOD_HEAD)) )
      return MHD_NO;    /* unexpected method (we're not polite...) */
    fd = -1;
    if ( (NULL == strstr (&url[1], "..")) &&
         ('/' != url[1]) )
    {
      fd = open (&url[1], O_RDONLY);
      if ( (-1 != fd) &&
           ( (0 != fstat (fd, &buf)) ||
             (! S_ISREG (buf.st_mode)) ) )
      {
        (void) close (fd);
        fd = -1;
      }
    }
    if (-1 == fd)
      return MHD_queue_response (connection,
                                 MHD_HTTP_NOT_FOUND,
                                 file_not_found_response);
#ifdef MHD_HAVE_LIBMAGIC
    /* read beginning of the file to determine mime type  */
    got = read (fd, file_data, sizeof (file_data));
    (void) lseek (fd, 0, SEEK_SET);
    if (0 < got)
      mime = magic_buffer (magic, file_data, (size_t) got);
    else
#endif /* MHD_HAVE_LIBMAGIC */
    mime = NULL;
    {
      /* Set mime-type by file-extension in some cases */
      const char *ldot = strrchr (&url[1], '.');

      if (NULL != ldot)
      {
        if (0 == strcasecmp (ldot,
                             ".html"))
          mime = "text/html";
        if (0 == strcasecmp (ldot,
                             ".css"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".css3"))
          mime = "text/css";
        if (0 == strcasecmp (ldot,
                             ".js"))
          mime = "application/javascript";
      }

    }

    if (NULL == (response = MHD_create_response_from_fd ((size_t) buf.st_size,
                                                         fd)))
    {
      /* internal error (i.e. out of memory) */
      (void) close (fd);
      return MHD_NO;
    }

    /* add mime type if we had one */
    if (NULL != mime)
      (void) MHD_add_response_header (response,
                                      MHD_HTTP_HEADER_CONTENT_TYPE,
                                      mime);
    ret = MHD_queue_response (connection,
                              MHD_HTTP_OK,
                              response);
    MHD_destroy_response (response);
    return ret;
  }

  /* Log right before the strcmp that checks for POST. This is the critical
     place where a NULL 'method' would cause strcmp(method, ...) to read
     through an invalid pointer (as reported). */
  log_probe("before_method_cmp_post", url, method, version,
            upload_data, upload_data_size, req_cls);

  if (0 == strcmp (method, MHD_HTTP_METHOD_POST))
  {
    /* upload! */
    struct UploadContext *uc = *req_cls;

    if (NULL == uc)
    {
      if (NULL == (uc = malloc (sizeof (struct UploadContext))))
        return MHD_NO; /* out of memory, close connection */
      memset (uc, 0, sizeof (struct UploadContext));
      uc->fd = -1;
      uc->connection = connection;
      uc->pp = MHD_create_post_processor (connection,
                                          64 * 1024 /* buffer size */,
                                          &process_upload_data, uc);
      if (NULL == uc->pp)
      {
        /* out of memory, close connection */
        free (uc);
        return MHD_NO;
      }
      *req_cls = uc;
      return MHD_YES;
    }
    if (0 != *upload_data_size)
    {
      if (NULL == uc->response)
        (void) MHD_post_process (uc->pp,
                                 upload_data,
                                 *upload_data_size);
      *upload_data_size = 0;
      return MHD_YES;
    }
    /* end of upload, finish it! */
    MHD_destroy_post_processor (uc->pp);
    uc->pp = NULL;
    if (-1 != uc->fd)
    {
      close (uc->fd);
      uc->fd = -1;
    }
    if (NULL != uc->response)
    {
      return MHD_queue_response (connection,
                                 MHD_HTTP_FORBIDDEN,
                                 uc->response);
    }
    else
    {
      update_directory ();
      return return_directory_response (connection);
    }
  }

  /* Log before the final GET/HEAD comparison near the end. */
  log_probe("before_method_cmp_final_get_head", url, method, version,
            upload_data, upload_data_size, req_cls);

  if ( (0 == strcmp (method, MHD_HTTP_METHOD_GET)) ||
       (0 == strcmp (method, MHD_HTTP_METHOD_HEAD)) )
  {
    return return_directory_response (connection);
  }

  /* unexpected request, refuse */
  return MHD_queue_response (connection,
                             MHD_HTTP_FORBIDDEN,
                             request_refused_response);
}
