#include <stdio.h>

/* Probe helper: append human-readable control-flow and state logs. */
static void
mhd_log_state(const char *stage, size_t r, size_t w, size_t uname_ext_len,
              size_t buf_size, const char *uname_ext)
{
  FILE *f = fopen("/tmp/logs/74.txt", "a");
  if (f)
  {
    char firstc = '.';
    if ((uname_ext != NULL) && (uname_ext_len > 0))
    {
      unsigned char uc = (unsigned char) uname_ext[0];
      if ((uc >= 32u) && (uc <= 126u))
        firstc = (char) uc;
      else
        firstc = '?';
    }
    fprintf(f, "%s: r=%zu w=%zu uname_ext_len=%zu buf_size=%zu uname_ext_first='%c'\n",
            stage, r, w, uname_ext_len, buf_size, firstc);
    fclose(f);
  }
}

static ssize_t
get_rq_extended_uname_copy_z (const char *uname_ext, size_t uname_ext_len,
                              char *buf, size_t buf_size)
{
  size_t r;
  size_t w;

  /* Log entry */
  mhd_log_state("entry", 0u, 0u, uname_ext_len, buf_size, uname_ext);

  if ((size_t) SSIZE_MAX < uname_ext_len)
  {
    mhd_log_state("return_too_long_input", 0u, 0u, uname_ext_len, buf_size, uname_ext);
    return -1; /* Too long input string */
  }

  if (MHD_DAUTH_EXT_PARAM_MIN_LEN > uname_ext_len)
  {
    mhd_log_state("return_missing_prefix", 0u, 0u, uname_ext_len, buf_size, uname_ext);
    return -1; /* Required prefix is missing */
  }

  if (! MHD_str_equal_caseless_bin_n_ (uname_ext, MHD_DAUTH_EXT_PARAM_PREFIX,
                                       MHD_STATICSTR_LEN_ ( \
                                         MHD_DAUTH_EXT_PARAM_PREFIX)))
  {
    mhd_log_state("return_prefix_mismatch", 0u, 0u, uname_ext_len, buf_size, uname_ext);
    return -1; /* Only UTF-8 is supported, as it is implied by RFC 7616 */
  }

  r = MHD_STATICSTR_LEN_ (MHD_DAUTH_EXT_PARAM_PREFIX);
  mhd_log_state("after_prefix_init", r, 0u, uname_ext_len, buf_size, uname_ext);

  /* Skip language tag */
  while (r < uname_ext_len && '\'' != uname_ext[r])
  {
    const char chr = uname_ext[r];
    if ((' ' == chr) || ('\t' == chr) || ('\"' == chr) || (',' == chr) ||
        (';' == chr) )
    {
      mhd_log_state("return_bad_char_in_lang_tag", r, 0u, uname_ext_len, buf_size, uname_ext);
      return -1; /* Wrong char in language tag */
    }
    r++;
  }

  if (r >= uname_ext_len)
  {
    mhd_log_state("return_lang_tag_not_found", r, 0u, uname_ext_len, buf_size, uname_ext);
    return -1; /* The end of the language tag was not found */
  }
  r++; /* Advance to the next char */
  mhd_log_state("after_lang_tag", r, 0u, uname_ext_len, buf_size, uname_ext);

  mhd_log_state("before_pct_decode", r, 0u, uname_ext_len, buf_size, uname_ext);
  w = MHD_str_pct_decode_strict_n_ (uname_ext + r, uname_ext_len - r,
                                    buf, buf_size);
  mhd_log_state("after_pct_decode", r, w, uname_ext_len, buf_size, uname_ext);

  if ((0 == w) && (0 != uname_ext_len - r))
  {
    mhd_log_state("return_broken_pct_encoding", r, w, uname_ext_len, buf_size, uname_ext);
    return -1; /* Broken percent encoding */
  }
  buf[w] = 0; /* Zero terminate the result */

  /* Log right before the mhd_assert to capture the state at that point */
  mhd_log_state("before_assert", r, w, uname_ext_len, buf_size, uname_ext);

  mhd_assert (SSIZE_MAX > w);

  /* And immediately after the assert to show it had no observable effect at runtime */
  mhd_log_state("after_assert", r, w, uname_ext_len, buf_size, uname_ext);

  return (ssize_t) w;
}
