#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <stdint.h>

/* Probe helper: append logs about control flow and state to /tmp/logs/74.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/74.txt", "a");
    if (f == NULL)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static ssize_t
get_rq_extended_uname_copy_z (const char *uname_ext, size_t uname_ext_len,
                              char *buf, size_t buf_size)
{
  size_t r;
  size_t w;

  mhd_probe_log("enter get_rq_extended_uname_copy_z: uname_ext=%p, uname_ext_len=%zu, buf=%p, buf_size=%zu",
                (const void *) uname_ext, uname_ext_len, (const void *) buf, buf_size);

  if ((size_t) SSIZE_MAX < uname_ext_len) {
    mhd_probe_log("early return: input too long: uname_ext_len=%zu, SSIZE_MAX=%zd", uname_ext_len, (ssize_t) SSIZE_MAX);
    return -1; /* Too long input string */
  }

  if (MHD_DAUTH_EXT_PARAM_MIN_LEN > uname_ext_len) {
    mhd_probe_log("early return: missing required prefix: uname_ext_len=%zu, min=%zu",
                  uname_ext_len, (size_t) MHD_DAUTH_EXT_PARAM_MIN_LEN);
    return -1; /* Required prefix is missing */
  }

  if (! MHD_str_equal_caseless_bin_n_ (uname_ext, MHD_DAUTH_EXT_PARAM_PREFIX,
                                       MHD_STATICSTR_LEN_ ( \
                                         MHD_DAUTH_EXT_PARAM_PREFIX)))
  {
    mhd_probe_log("early return: prefix mismatch (caseless bin n)");
    return -1; /* Only UTF-8 is supported, as it is implied by RFC 7616 */
  }

  r = MHD_STATICSTR_LEN_ (MHD_DAUTH_EXT_PARAM_PREFIX);
  mhd_probe_log("after prefix: r=%zu", r);

  /* Skip language tag */
  while (r < uname_ext_len && '\'' != uname_ext[r])
  {
    const char chr = uname_ext[r];
    mhd_probe_log("language-tag scan: r=%zu chr=0x%02x ('%c')", r, (unsigned char) chr, ((' ' <= chr && chr <= '~') ? chr : '?'));
    if ((' ' == chr) || ('\t' == chr) || ('\"' == chr) || (',' == chr) ||
        (';' == chr) )
    {
      mhd_probe_log("early return: wrong char in language tag at r=%zu chr=0x%02x", r, (unsigned char) chr);
      return -1; /* Wrong char in language tag */
    }
    r++;
  }
  if (r >= uname_ext_len) {
    mhd_probe_log("early return: end of language tag not found: r=%zu uname_ext_len=%zu", r, uname_ext_len);
    return -1; /* The end of the language tag was not found */
  }
  r++; /* Advance to the next char */
  mhd_probe_log("after language tag: r=%zu remaining=%zu", r, uname_ext_len - r);

  w = MHD_str_pct_decode_strict_n_ (uname_ext + r, uname_ext_len - r,
                                    buf, buf_size);
  mhd_probe_log("percent-decode result: w=%zu (decoded bytes), remaining_input=%zu", w, uname_ext_len - r);

  if ((0 == w) && (0 != uname_ext_len - r))
  {
    mhd_probe_log("early return: broken percent encoding: w=%zu remaining=%zu", w, uname_ext_len - r);
    return -1; /* Broken percent encoding */
  }
  buf[w] = 0; /* Zero terminate the result */
  mhd_probe_log("buffer terminated: buf[%zu]=0; about to assert SSIZE_MAX > w (SSIZE_MAX=%zd, w=%zu)", w, (ssize_t) SSIZE_MAX, w);

  /* The original assertion is preserved exactly as in the target code. */
  mhd_assert (SSIZE_MAX > w);

  /* Log after assertion to provide evidence whether the assertion call executed (even if it is a no-op in the build). */
  mhd_probe_log("after mhd_assert: continuing to return w=%zu", w);

  return (ssize_t) w;
}
