static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;

  buf_used = 0;

  /* Capture the result of get_rq_uname_type once to avoid extra calls */
  enum MHD_DigestAuthUsernameType inferred_type = get_rq_uname_type (params);

  /* Log entry and the evaluated conditions that would be asserted */
  mhd_log_probe("ENTRY: get_rq_uname params=%p uname_type=%d inferred_type=%d buf=%p buf_size=%zu",
                (void *)params,
                (int) uname_type,
                (int) inferred_type,
                (void *) buf,
                buf_size);

  mhd_log_probe("CHECK: inferred_type == uname_type -> %d",
                (int) (inferred_type == uname_type));
  mhd_assert (inferred_type == uname_type);

  mhd_log_probe("CHECK: uname_type != INVALID -> %d",
                (int) (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type));
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);

  mhd_log_probe("CHECK: uname_type != MISSING -> %d",
                (int) (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type));
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  mhd_log_probe("STATE: after init buf_used=%zu", buf_used);

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    uname_info->username = (char *) (buf + buf_used);
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);
    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;

    mhd_log_probe("BRANCH: STANDARD username_len=%zu buf_used=%zu",
                  uname_info->username_len, buf_used);
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    size_t res;

    uname_info->userhash_hex = (char *) (buf + buf_used);
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);
    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);
    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);

    mhd_log_probe("BRANCH: USERHASH userhash_hex_len=%zu res=%zu buf_used(before_bin)=%zu",
                  uname_info->userhash_hex_len, res, buf_used);

    if (res != uname_info->userhash_hex_len / 2)
    {
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
      mhd_log_probe("USERHASH: invalid hex length -> set uname_type=INVALID");
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;
      mhd_log_probe("USERHASH: ok buf_used(after_bin)=%zu uname_type=USERHASH",
                    buf_used);
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    ssize_t res;
    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    mhd_log_probe("BRANCH: EXTENDED get_rq_extended_uname_copy_z returned=%zd",
                  res);
    if (0 > res)
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;
      mhd_log_probe("EXTENDED: username_len=%zu buf_used=%zu",
                    uname_info->username_len, buf_used);
    }
  }
  else
  {
    /* Log that the previously-asserted impossible branch is taken. */
    mhd_log_probe("BRANCH: ELSE reached for uname_type=%d -> calling mhd_assert(0)", (int) uname_type);
    mhd_assert (0);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    mhd_log_probe("ELSE: set uname_type=INVALID");
  }

  mhd_log_probe("EXIT CHECK: buf_size=%zu buf_used=%zu -> buf_size>=buf_used? %d",
                buf_size, buf_used, (int) (buf_size >= buf_used));
  mhd_assert (buf_size >= buf_used);

  mhd_log_probe("RETURN: buf_used=%zu", buf_used);
  return buf_used;
}

