#include <stddef.h>
#include <stdint.h>
#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append messages to /tmp/logs/18.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/18.txt", "a");
    if (f == NULL)
        return;

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;

  buf_used = 0;

  /* Log the initial call context */
  mhd_probe_log("get_rq_uname: params=%p uname_type=%d buf=%p buf_size=%zu",
                (void *) params, (int) uname_type, (void *) buf, buf_size);

  /* Evaluate and log the expressions that were originally inside mhd_assert.
     These evaluations are for probing only and do not alter control flow. */
  {
    int probe_expr1 = (get_rq_uname_type (params) == uname_type);
    int probe_expr2 = (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
    int probe_expr3 = (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

    mhd_probe_log("probe: get_rq_uname_type(params)==uname_type -> %d", probe_expr1);
    mhd_probe_log("probe: uname_type != INVALID -> %d", probe_expr2);
    mhd_probe_log("probe: uname_type != MISSING -> %d", probe_expr3);
  }

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    uname_info->username = (char *) (buf + buf_used);
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);
    mhd_probe_log("branch: STANDARD taken; username_len=%zu", uname_info->username_len);
    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    size_t res;

    uname_info->userhash_hex = (char *) (buf + buf_used);
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);
    mhd_probe_log("branch: USERHASH taken; userhash_hex_len=%zu",
                  uname_info->userhash_hex_len);
    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);
    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);
    mhd_probe_log("MHD_hex_to_bin -> res=%zu (expected=%zu)",
                  res, uname_info->userhash_hex_len / 2);
    if (res != uname_info->userhash_hex_len / 2)
    {
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
      mhd_probe_log("USERHASH: hex->bin size mismatch; marked INVALID");
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;
      mhd_probe_log("USERHASH: conversion ok; buf_used now=%zu", buf_used);
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    ssize_t res;
    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    mhd_probe_log("branch: EXTENDED taken; get_rq_extended_uname_copy_z -> %zd", res);
    if (0 > res)
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;
      mhd_probe_log("EXTENDED: username_len=%zu buf_used=%zu",
                    uname_info->username_len, buf_used);
    }
  }
  else
  {
    /* original code had mhd_assert(0); here — probe and log that this branch was taken */
    mhd_probe_log("branch: UNKNOWN uname_type=%d -> marking INVALID", (int) uname_type);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
  }

  /* Log and probe the final buffer-size check that was originally asserted. */
  {
    int final_check = (buf_size >= buf_used);
    mhd_probe_log("final probe: buf_size=%zu buf_used=%zu check(buf_size>=buf_used)=%d",
                  buf_size, buf_used, final_check);
  }

  return buf_used;
}
