static void
mhd_log_probe(const char *stage,
              const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              const uint8_t *buf,
              size_t buf_size,
              size_t buf_used)
{
  FILE *f = fopen("/tmp/logs/23.txt", "a");
  if (NULL == f)
    return;

  fprintf(f, "STAGE=%s uname_type=%d buf=%p buf_size=%zu buf_used=%zu\n",
          stage, (int) uname_type, (const void *) buf, buf_size, buf_used);
  if (NULL != params)
  {
    /* Log username parameter length and a short hex/printable prefix (safe bounded read). */
    size_t plen = (size_t) params->username.value.len;
    fprintf(f, "  param.username.value.len=%zu\n", plen);
    if (0 < plen && NULL != params->username.value.str)
    {
      size_t to_log = plen > 32 ? 32 : plen;
      fprintf(f, "  param.username.value.str[0..%zu]=\"", to_log);
      for (size_t i = 0; i < to_log; ++i)
      {
        unsigned char c = (unsigned char) params->username.value.str[i];
        /* print printable ASCII directly, else hex escape */
        if (c >= 0x20 && c < 0x7f)
          fputc(c, f);
        else
          fprintf(f, "\\x%02x", c);
      }
      if (plen > to_log)
        fprintf(f, "... (truncated)\"");
      else
        fprintf(f, "\"");
      fprintf(f, "\n");
    }
  }
  fflush(f);
  fclose(f);
}

static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;

  buf_used = 0;
  mhd_assert (get_rq_uname_type (params) == uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  /* Log entry state */
  mhd_log_probe("entry", params, uname_type, buf, buf_size, buf_used);

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    /* Log before the risky copy */
    mhd_log_probe("before_standard_copy", params, uname_type, buf, buf_size, buf_used);

    uname_info->username = (char *) (buf + buf_used);
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);

    buf_used += uname_info->username_len + 1;

    /* Log after the copy */
    mhd_log_probe("after_standard_copy", params, uname_type, buf, buf_size, buf_used);

    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    size_t res;

    /* Log before userhash processing */
    mhd_log_probe("before_userhash_copy", params, uname_type, buf, buf_size, buf_used);

    uname_info->userhash_hex = (char *) (buf + buf_used);
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);
    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);
    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);

    /* Log intermediate results */
    mhd_log_probe("after_userhash_hex_to_bin", params, uname_type, buf, buf_size, buf_used);

    if (res != uname_info->userhash_hex_len / 2)
    {
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;

      /* Log invalid conversion */
      mhd_log_probe("userhash_invalid", params, uname_type, buf, buf_size, buf_used);
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;

      /* Log successful userhash handling */
      mhd_log_probe("userhash_success", params, uname_type, buf, buf_size, buf_used);
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    ssize_t res;

    /* Log before extended uname processing */
    mhd_log_probe("before_extended_copy", params, uname_type, buf, buf_size, buf_used);

    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    if (0 > res)
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;

      /* Log after extended copy */
      mhd_log_probe("after_extended_copy", params, uname_type, buf, buf_size, buf_used);
    }
  }
  else
  {
    mhd_assert (0);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
  }
  mhd_assert (buf_size >= buf_used);

  /* Log exit state */
  mhd_log_probe("exit", params, uname_type, buf, buf_size, buf_used);

  return buf_used;
}
