#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>

/* Probe/log helper: append human-readable logs to /tmp/logs/19.txt */
static void
mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/19.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static size_t
get_rq_uname (const struct MHD_RqDAuth *params,
              enum MHD_DigestAuthUsernameType uname_type,
              struct MHD_DigestAuthUsernameInfo *uname_info,
              uint8_t *buf,
              size_t buf_size)
{
  size_t buf_used;

  /* Log entry and key inputs */
  mhd_probe_log("ENTER get_rq_uname: params=%p uname_type=%d uname_info=%p buf=%p buf_size=%zu",
                (void *) params, (int) uname_type, (void *) uname_info, (void *) buf, buf_size);

  buf_used = 0;
  mhd_assert (get_rq_uname_type (params) == uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_INVALID != uname_type);
  mhd_assert (MHD_DIGEST_AUTH_UNAME_TYPE_MISSING != uname_type);

  uname_info->username = NULL;
  uname_info->username_len = 0;
  uname_info->userhash_hex = NULL;
  uname_info->userhash_hex_len = 0;
  uname_info->userhash_bin = NULL;

  mhd_probe_log("initialised uname_info: username=%p username_len=%zu userhash_hex=%p userhash_hex_len=%zu userhash_bin=%p",
                (void *) uname_info->username, uname_info->username_len,
                (void *) uname_info->userhash_hex, uname_info->userhash_hex_len,
                (void *) uname_info->userhash_bin);

  if (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD == uname_type)
  {
    mhd_probe_log("branch: STANDARD");
    uname_info->username = (char *) (buf + buf_used);
    uname_info->username_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->username);
    mhd_probe_log("copied username: dest=%p len=%zu",
                  (void *) uname_info->username, uname_info->username_len);
    buf_used += uname_info->username_len + 1;
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD;
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH == uname_type)
  {
    size_t res;

    mhd_probe_log("branch: USERHASH");
    uname_info->userhash_hex = (char *) (buf + buf_used);
    uname_info->userhash_hex_len =
      get_rq_param_unquoted_copy_z (&params->username,
                                    uname_info->userhash_hex);
    mhd_probe_log("copied userhash_hex: dest=%p hex_len=%zu (hex content pointer)",
                  (void *) uname_info->userhash_hex, uname_info->userhash_hex_len);
    buf_used += uname_info->userhash_hex_len + 1;
    uname_info->userhash_bin = (uint8_t *) (buf + buf_used);

    mhd_probe_log("about to call MHD_hex_to_bin: hex_ptr=%p hex_len=%zu bin_ptr=%p",
                  (void *) uname_info->userhash_hex, uname_info->userhash_hex_len,
                  (void *) uname_info->userhash_bin);

    res = MHD_hex_to_bin (uname_info->userhash_hex,
                          uname_info->userhash_hex_len,
                          uname_info->userhash_bin);

    mhd_probe_log("MHD_hex_to_bin returned: res=%zu (expected=%zu)",
                  res, uname_info->userhash_hex_len / 2);

    if (res != uname_info->userhash_hex_len / 2)
    {
      mhd_probe_log("hex conversion failed: invalid length, marking invalid");
      uname_info->userhash_bin = NULL;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    }
    else
    {
      /* Avoid pointers outside allocated region when the size is zero */
      if (0 == res)
        uname_info->userhash_bin = (uint8_t *) uname_info->username;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_USERHASH;
      buf_used += res;
      mhd_probe_log("hex conversion succeeded: userhash_bin=%p buf_used=%zu uname_type=%d",
                    (void *) uname_info->userhash_bin, buf_used, (int) uname_info->uname_type);
    }
  }
  else if (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED == uname_type)
  {
    ssize_t res;
    mhd_probe_log("branch: EXTENDED (username_ext at %p)", (void *) &params->username_ext);
    res = get_rq_extended_uname_copy_z (params->username_ext.value.str,
                                        params->username_ext.value.len,
                                        (char *) (buf + buf_used),
                                        buf_size - buf_used);
    mhd_probe_log("get_rq_extended_uname_copy_z returned: res=%zd", res);
    if (0 > res)
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    else
    {
      uname_info->username = (char *) (buf + buf_used);
      uname_info->username_len = (size_t) res;
      uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED;
      buf_used += uname_info->username_len + 1;
      mhd_probe_log("extended username copied: username=%p username_len=%zu buf_used=%zu",
                    (void *) uname_info->username, uname_info->username_len, buf_used);
    }
  }
  else
  {
    mhd_assert (0);
    uname_info->uname_type = MHD_DIGEST_AUTH_UNAME_TYPE_INVALID;
    mhd_probe_log("branch: UNKNOWN - asserted and marked invalid");
  }
  mhd_assert (buf_size >= buf_used);

  mhd_probe_log("EXIT get_rq_uname: final uname_type=%d buf_used=%zu username=%p username_len=%zu userhash_hex=%p userhash_hex_len=%zu userhash_bin=%p",
                (int) uname_info->uname_type, buf_used,
                (void *) uname_info->username, uname_info->username_len,
                (void *) uname_info->userhash_hex, uname_info->userhash_hex_len,
                (void *) uname_info->userhash_bin);
  return buf_used;
}
