#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append human-readable trace to /tmp/logs/146.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/146.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  /* Log entry state (do not dereference r beyond printing the pointer to
     avoid changing behaviour when assertions are compiled out). */
  mhd_probe_log("keepalive_possible: entry: connection=%p c->keepalive=%d c->rq.http_ver=%d r=%p",
                (void *) connection,
                (int) c->keepalive,
                (int) c->rq.http_ver,
                (void *) r);

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    mhd_probe_log("keepalive_possible: early: c->keepalive forces close (value=%d)", (int) c->keepalive);
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* Log that we entered the upgrade-handler branch and capture related state */
    mhd_probe_log("keepalive_possible: upgrade path: r=%p r->upgrade_handler=%p r->flags_auto=0x%08x c->rq.http_ver=%d c->stop_with_error=%d",
                  (void *) r,
                  (void *) r->upgrade_handler,
                  (unsigned int) r->flags_auto,
                  (int) c->rq.http_ver,
                  (int) c->stop_with_error);

    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);
    mhd_probe_log("keepalive_possible: upgrade path -> MUST_UPGRADE");
    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    mhd_probe_log("keepalive_possible: read_closed=%d discard_request=%d -> MUST_CLOSE",
                  (int) c->read_closed,
                  (int) c->discard_request);
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    mhd_probe_log("keepalive_possible: response flags indicate 1.0 strict (flags=0x%08x) -> MUST_CLOSE",
                  (unsigned int) r->flags);
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    mhd_probe_log("keepalive_possible: response flags_auto indicate Connection: close (flags_auto=0x%08x) -> MUST_CLOSE",
                  (unsigned int) r->flags_auto);
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    mhd_probe_log("keepalive_possible: HTTP version unsupported (rq.http_ver=%d) -> MUST_CLOSE",
                  (int) c->rq.http_ver);
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_lookup_header_s_token_ci (c,
                                    MHD_HTTP_HEADER_CONNECTION,
                                    "close"))
  {
    mhd_probe_log("keepalive_possible: found Connection: close header -> MUST_CLOSE (http_ver=%d)", (int) c->rq.http_ver);
    return MHD_CONN_MUST_CLOSE;
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    /* For 1.0, check for Keep-Alive token */
    if (MHD_lookup_header_s_token_ci (connection,
                                      MHD_HTTP_HEADER_CONNECTION,
                                      "Keep-Alive"))
    {
      mhd_probe_log("keepalive_possible: HTTP/1.0 and found Keep-Alive header -> USE_KEEPALIVE");
      return MHD_CONN_USE_KEEPALIVE;
    }

    mhd_probe_log("keepalive_possible: HTTP/1.0 and no Keep-Alive -> MUST_CLOSE");
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    mhd_probe_log("keepalive_possible: HTTP/1.1 compatible -> USE_KEEPALIVE (http_ver=%d)", (int) c->rq.http_ver);
    return MHD_CONN_USE_KEEPALIVE;
  }

  mhd_probe_log("keepalive_possible: default -> MUST_CLOSE (http_ver=%d)", (int) c->rq.http_ver);
  return MHD_CONN_MUST_CLOSE;
}
