#include <stdio.h>
#include <stdarg.h>
#include <errno.h>

/* Probe helper: append formatted messages to /tmp/logs/282.txt. */
static void probe_log(const char *tag, const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/282.txt", "a");
  if (!f)
    return;
  if (tag)
    fprintf(f, "%s: ", tag);
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static int
mhd_tool_get_sys_cpu_count_sysctl_fallback_ (void)
{
  int ret = -1;
  probe_log("ENTRY", "initial ret=%d", ret);
  /* Do not use sysctl() function on GNU/Linux even if
     sysctl() is available */
#ifndef __linux__
#ifdef HAVE_SYSCTLBYNAME
  if (0 >= ret)
  {
    size_t value_size = sizeof (ret);
    /* FreeBSD, OpenBSD, NetBSD, Darwin (and others?): The number of CPUs */
    probe_log("SYSCTLBYNAME_BEFORE", "ret=%d, value_size=%zu", ret, value_size);
    if ((0 != sysctlbyname ("hw.ncpu", &ret, &value_size,
                            NULL, 0))
        || (sizeof (ret) != value_size))
    {
      probe_log("SYSCTLBYNAME_FAIL", "after sysctlbyname: ret=%d, value_size=%zu, errno=%d", ret, value_size, errno);
      ret = -1;
    }
    else
    {
      probe_log("SYSCTLBYNAME_OK", "after sysctlbyname: ret=%d, value_size=%zu", ret, value_size);
    }
  }
#endif /* HAVE_SYSCTLBYNAME */
#if defined(HAVE_SYSCTL) && \
  defined(HAS_DECL_CTL_HW) && \
  defined(HAS_DECL_HW_NCPU)
  if (0 >= ret)
  {
    /* FreeBSD, OpenBSD, NetBSD, Darwin (and others?): The number of CPUs */
    int mib[2] = {CTL_HW, HW_NCPU};
    size_t value_size = sizeof (ret);
    probe_log("SYSCTL_BEFORE", "ret=%d, value_size=%zu, mib=[%d,%d]", ret, value_size, mib[0], mib[1]);
    if ((0 != sysctl (mib, 2, &ret, &value_size, NULL, 0))
        || (sizeof (ret) != value_size))
    {
      probe_log("SYSCTL_FAIL", "after sysctl: ret=%d, value_size=%zu, errno=%d", ret, value_size, errno);
      ret = -1;
    }
    else
    {
      probe_log("SYSCTL_OK", "after sysctl: ret=%d, value_size=%zu", ret, value_size);
    }
  }
#endif /* HAVE_SYSCTL && HAS_DECL_CTL_HW && HAS_DECL_HW_NCPU */
#endif /* ! __linux__ */
  if (0 >= ret)
  {
    probe_log("RETURN_NEG", "ret=%d", ret);
    return -1;
  }
  probe_log("RETURN_OK", "ret=%d", ret);
  return ret;
}
