#include <stdio.h>
#include <stdarg.h>

static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/194.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  mhd_probe_log("parse_cookie_header: entry connection=%p saved_tail=%p daemon=%p client_discipline=%d",
                (void *)connection,
                (void *)saved_tail,
                (void *)connection->daemon,
                connection->daemon ? connection->daemon->client_discipline : -1);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
  {
    mhd_probe_log("parse_cookie_header: no Cookie header present -> returning MHD_PARSE_COOKIE_OK");
    return MHD_PARSE_COOKIE_OK;
  }

  mhd_probe_log("parse_cookie_header: got Cookie header at %p len=%zu", (void *)hdr, hdr_len);

  if (0 == hdr_len)
  {
    mhd_probe_log("parse_cookie_header: hdr_len == 0 -> returning MHD_PARSE_COOKIE_OK");
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
  {
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
    mhd_probe_log("parse_cookie_header: allocation failed for len=%zu -> parse_res=MHD_PARSE_COOKIE_NO_MEMORY", hdr_len);
  }
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    mhd_probe_log("parse_cookie_header: calling parse_cookies_string with offset=%zu remaining=%zu", i, hdr_len - i);
    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);
    mhd_probe_log("parse_cookie_header: parse_cookies_string returned %d saved_tail=%p new_tail=%p",
                  (int) parse_res,
                  (void *) saved_tail,
                  (void *) connection->rq.headers_received_tail);
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    mhd_probe_log("parse_cookie_header: parse_res == MHD_PARSE_COOKIE_OK");
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("parse_cookie_header: parse_res == MHD_PARSE_COOKIE_OK_LAX saved_tail=%p new_tail=%p",
                  (void *) saved_tail,
                  (void *) connection->rq.headers_received_tail);
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    mhd_probe_log("parse_cookie_header: parse_res == MHD_PARSE_COOKIE_MALFORMED saved_tail=%p new_tail=%p allow_partially_correct_cookie=%d",
                  (void *) saved_tail,
                  (void *) connection->rq.headers_received_tail,
                  allow_partially_correct_cookie);
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
        mhd_probe_log("parse_cookie_header: malformed cookie -> extracted values removed; restored saved_tail=%p", (void *) saved_tail);
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("parse_cookie_header: parse_res == MHD_PARSE_COOKIE_NO_MEMORY");
    break;
  default:
    /* Log the unexpected parse_res so tools/agents can observe this path.
       Keep the original assertion call to preserve original code. */
    mhd_probe_log("parse_cookie_header: default: unexpected parse_res=%d -> invoking mhd_assert(0)", (int) parse_res);
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  mhd_probe_log("parse_cookie_header: exit parse_res=%d", (int) parse_res);

  return parse_res;
}
