#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append diagnostic information to /tmp/logs/194.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/194.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  /* Log entry and key connection state */
  mhd_probe_log("parse_cookie_header: entry connection=%p, daemon=%p, client_discipline=%d, allow_partially_correct_cookie=%d, saved_tail=%p",
                (void *)connection,
                (void *)connection->daemon,
                (int)connection->daemon->client_discipline,
                (int)allow_partially_correct_cookie,
                (void *)saved_tail);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len)) {
    mhd_probe_log("parse_cookie_header: no Cookie header found; returning OK");
    return MHD_PARSE_COOKIE_OK;
  }
  if (0 == hdr_len) {
    mhd_probe_log("parse_cookie_header: Cookie header present but length 0; returning OK");
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy) {
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
    mhd_probe_log("parse_cookie_header: allocation failed for hdr_len=%zu -> NO_MEMORY", hdr_len);
  }
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    mhd_probe_log("parse_cookie_header: about to call parse_cookies_string; hdr_len=%zu, skip=%zu, remaining=%zu, cpy+skip='%s'",
                  hdr_len, i, hdr_len - i, cpy + i);

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);

    mhd_probe_log("parse_cookie_header: parse_cookies_string returned parse_res=%d", (int)parse_res);
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    mhd_probe_log("parse_cookie_header: branch MHD_PARSE_COOKIE_OK");
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("parse_cookie_header: branch MHD_PARSE_COOKIE_OK_LAX; saved_tail changed=%d",
                  saved_tail != connection->rq.headers_received_tail);
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    mhd_probe_log("parse_cookie_header: branch MHD_PARSE_COOKIE_MALFORMED; saved_tail_changed=%d, allow_partially_correct_cookie=%d",
                  saved_tail != connection->rq.headers_received_tail,
                  (int)allow_partially_correct_cookie);
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("parse_cookie_header: branch MHD_PARSE_COOKIE_NO_MEMORY");
    break;
  default:
    /* Log detailed state immediately before the assertion so that, when
       mhd_assert is compiled out, there is still evidence that this path
       was executed and what the state was. */
    mhd_probe_log("parse_cookie_header: default branch reached; unexpected parse_res=%d, connection=%p, saved_tail=%p",
                  (int)parse_res,
                  (void *)connection,
                  (void *)saved_tail);
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  mhd_probe_log("parse_cookie_header: exit parse_res=%d", (int)parse_res);

  return parse_res;
}
