#include <stdio.h>
#include <string.h>
#include <stddef.h>

/* Probe helper: append a single-line log to /tmp/logs/194.txt. */
static void
mhd_probe_log (const char *stage,
               struct MHD_Connection *connection,
               const char *hdr,
               size_t hdr_len,
               const char *cpy,
               size_t i,
               enum _MHD_ParseCookie parse_res,
               struct MHD_HTTP_Req_Header *saved_tail)
{
  FILE *f = fopen("/tmp/logs/194.txt", "a");
  if (NULL == f)
    return;

  /* Safely print a short preview of header contents. */
  char hdr_preview[129] = { '\0' };
  if (hdr != NULL && hdr_len > 0)
  {
    size_t to_copy = hdr_len < (sizeof (hdr_preview) - 1) ? hdr_len : (sizeof (hdr_preview) - 1);
    memcpy (hdr_preview, hdr, to_copy);
    hdr_preview[to_copy] = '\0';
  }

  /* cpy is NUL-terminated when present; i is the whitespace-skipping index. */
  fprintf (f,
           "stage=%s conn=%p daemon=%p saved_tail=%p hdr_len=%zu hdr_preview=\"%s\" cpy=%p i=%zu parse_res=%d\n",
           stage,
           (void *) connection,
           (void *) (connection ? connection->daemon : NULL),
           (void *) saved_tail,
           hdr_len,
           hdr_preview,
           (void *) cpy,
           i,
           (int) parse_res);

  fclose (f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  /* Log function entry state */
  mhd_probe_log ("entry", connection, NULL, 0, NULL, 0, (enum _MHD_ParseCookie) -1, saved_tail);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
  {
    /* Log that no Cookie header was found */
    mhd_probe_log ("no_header", connection, NULL, 0, NULL, 0, MHD_PARSE_COOKIE_OK, saved_tail);
    return MHD_PARSE_COOKIE_OK;
  }

  /* Log header presence and a preview */
  mhd_probe_log ("header_found", connection, hdr, hdr_len, NULL, 0, (enum _MHD_ParseCookie) -1, saved_tail);

  if (0 == hdr_len)
  {
    mhd_probe_log ("empty_header_len", connection, hdr, hdr_len, NULL, 0, MHD_PARSE_COOKIE_OK, saved_tail);
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    /* Log the buffer that will be parsed and the index after whitespace skip */
    mhd_probe_log ("before_parse", connection, hdr, hdr_len, cpy, i, (enum _MHD_ParseCookie) -1, saved_tail);

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);

    /* Log result from parser */
    mhd_probe_log ("after_parse", connection, hdr, hdr_len, cpy, i, parse_res, saved_tail);
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    break;
  default:
    /* Log that the default branch was reached. This is the branch that
     * contains the mhd_assert(0) call in the original code. When mhd_assert
     * is defined as ((void)0) this statement becomes an executed no-op.
     * Recording that the default case was reached provides runtime evidence
     * of that behavior.
     */
    mhd_probe_log ("default_case_reached", connection, hdr, hdr_len, NULL, 0, parse_res, saved_tail);
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  /* Final log before returning */
  mhd_probe_log ("return", connection, hdr, hdr_len, NULL, 0, parse_res, saved_tail);

  return parse_res;
}
