static void
mhd_probe_log_event (const char *event,
                     struct MHD_Connection *connection,
                     size_t hdr_len,
                     int parse_res,
                     int allow_partially)
{
  /* Append human-readable probe information to the evidence file. */
  FILE *f = fopen ("/tmp/logs/194.txt", "a");
  if (NULL == f)
    return;
  fprintf (f,
           "EVENT=%s conn=%p headers_received_tail=%p hdr_len=%zu parse_res=%d allow_partially=%d\n",
           event,
           (void *) connection,
           /* Note: do not reference the local variable `saved_tail` here to avoid
              changing the usage profile of that local in the build without
              HAVE_MESSAGES. Log the connection field instead. */
           (void *) connection->rq.headers_received_tail,
           hdr_len,
           parse_res,
           allow_partially);
  fclose (f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  /* Probe: entry state */
  mhd_probe_log_event ("enter_parse_cookie_header",
                       connection,
                       0u, /* hdr_len unknown at entry */
                       -1, /* parse_res unknown */
                       allow_partially_correct_cookie ? 1 : 0);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
  {
    /* Probe: no Cookie header found */
    mhd_probe_log_event ("no_cookie_header",
                         connection,
                         0u,
                         (int) MHD_PARSE_COOKIE_OK,
                         allow_partially_correct_cookie ? 1 : 0);
    return MHD_PARSE_COOKIE_OK;
  }
  if (0 == hdr_len)
  {
    /* Probe: empty Cookie header */
    mhd_probe_log_event ("empty_cookie_header",
                         connection,
                         0u,
                         (int) MHD_PARSE_COOKIE_OK,
                         allow_partially_correct_cookie ? 1 : 0);
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    /* Probe: before parsing cookies string */
    mhd_probe_log_event ("before_parse_cookies_string",
                         connection,
                         hdr_len - i,
                         -1,
                         allow_partially_correct_cookie ? 1 : 0);

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    break;
  default:
    mhd_assert (0);
    break;
  }

  /* Probe: exit state. Note: avoid referencing the local `saved_tail` here in
     the build without HAVE_MESSAGES, to keep the original usage profile of
     that local unchanged. Instead log the connection field. */
  mhd_probe_log_event ("exit_parse_cookie_header",
                       connection,
                       hdr_len,
                       (int) parse_res,
                       allow_partially_correct_cookie ? 1 : 0);

#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  return parse_res;
}
