#include <stdio.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper. Does not alter program logic. */
static void
mhd_probe_log (const char *label,
               const void *connection_ptr,
               const void *saved_tail_ptr,
               int parse_res_val,
               const char *hdr_ptr,
               size_t hdr_len_val,
               const char *note)
{
  FILE *f = fopen("/tmp/logs/194.txt", "a");
  if (f == NULL)
    return;
  fprintf (f, "[probe] %s\n", label);
  fprintf (f, "  connection=%p\n", connection_ptr);
  fprintf (f, "  saved_tail=%p\n", saved_tail_ptr);
  fprintf (f, "  parse_res=%d\n", parse_res_val);
  if (hdr_ptr != NULL)
    fprintf (f, "  hdr_ptr=%p hdr_len=%zu\n", hdr_ptr, hdr_len_val);
  if (note != NULL)
    fprintf (f, "  note=%s\n", note);
  fprintf (f, "\n");
  fclose (f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  /* Log entry and saved_tail state */
  mhd_probe_log ("ENTRY", connection, saved_tail, -1, NULL, 0, NULL);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
  {
    mhd_probe_log ("NO_COOKIE_HEADER", connection, saved_tail, -1, NULL, 0,
                   "MHD_lookup_connection_value_n returned MHD_NO");
    return MHD_PARSE_COOKIE_OK;
  }
  if (0 == hdr_len)
  {
    mhd_probe_log ("EMPTY_COOKIE_HEADER", connection, saved_tail, -1, hdr,
                   hdr_len, "Header length is zero");
    return MHD_PARSE_COOKIE_OK;
  }

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
  {
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
    mhd_probe_log ("ALLOC_FAIL", connection, saved_tail, parse_res, hdr,
                   hdr_len, "MHD_connection_alloc_memory_ returned NULL");
  }
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    mhd_probe_log ("BEFORE_PARSE", connection, saved_tail, -1, cpy + i,
                   hdr_len - i, "About to call parse_cookies_string");

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);

    mhd_probe_log ("AFTER_PARSE", connection, saved_tail, (int) parse_res,
                   cpy + i, hdr_len - i, "parse_cookies_string returned");
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    break;
  default:
    /* Log that we are entering the default (unexpected) branch. This captures
       the control path that will execute the mhd_assert(0) statement which
       may be compiled out to a no-op. */
    mhd_probe_log ("DEFAULT_BRANCH_ENTER", connection, saved_tail,
                   (int) parse_res, NULL, 0,
                   "About to execute mhd_assert(0) - may be no-op when disabled");
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  mhd_probe_log ("EXIT", connection, saved_tail, (int) parse_res, hdr,
                 hdr_len, "Returning from parse_cookie_header");

  return parse_res;
}
