#include <stdio.h>

/* Probe helper to log state to /tmp/logs/199.txt */
static void MHD_probe_log_cookie_state(const char *stage,
                                       struct MHD_Connection *connection,
                                       const char *hdr,
                                       size_t hdr_len,
                                       size_t alloc_req,
                                       const void *alloc_ptr)
{
  FILE *f = fopen("/tmp/logs/199.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "[parse_cookie_header] %s: connection=%p hdr=%p hdr_len=%zu alloc_req=%zu alloc_ptr=%p\n",
          stage,
          (void *) connection,
          (const void *) hdr,
          hdr_len,
          alloc_req,
          alloc_ptr);
  /* Dump a small prefix of the header to help reproduce the issue, but
     avoid reading beyond hdr_len. */
  if (hdr != NULL && hdr_len > 0)
  {
    size_t dump = hdr_len < 64 ? hdr_len : 64;
    fprintf(f, " hdr_prefix(%zu)='", dump);
    fwrite(hdr, 1, dump, f);
    fprintf(f, "'\n");
  }
  fclose(f);
}

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  size_t alloc_req;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
    return MHD_PARSE_COOKIE_OK;
  if (0 == hdr_len)
    return MHD_PARSE_COOKIE_OK;

  /* Log state after header lookup and before allocation. */
  alloc_req = hdr_len + 1; /* preserve original allocation intent */
  MHD_probe_log_cookie_state("after_lookup", connection, hdr, hdr_len, alloc_req, NULL);

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);

  /* Log allocation result and relevant state. This captures the pointer
     returned and the requested allocation size so agents can inspect
     possible integer wrap-around or allocator behaviors. */
  MHD_probe_log_cookie_state("after_alloc", connection, hdr, hdr_len, alloc_req, (const void *) cpy);

  if (NULL == cpy)
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
  else
  {
    /* Log immediately before copying to capture hdr_len and cpy pointer.
       This helps reproduce cases where hdr_len is so large that the
       allocation size wrapped or the allocator returned a smaller buffer. */
    MHD_probe_log_cookie_state("before_memcpy", connection, hdr, hdr_len, alloc_req, (const void *) cpy);

    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    /* Log after copy completion. (May not be reached if memcpy overflows.) */
    MHD_probe_log_cookie_state("after_memcpy", connection, hdr, hdr_len, alloc_req, (const void *) cpy);

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    break;
  default:
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  return parse_res;
}
