static void
post_iterator_probe (struct Session *session,
                     enum MHD_ValueKind kind,
                     const char *filename,
                     const char *content_type,
                     const char *transfer_encoding,
                     const char *key,
                     const char *data, uint64_t off, size_t size)
{
  FILE *f = fopen("/tmp/logs/386.txt", "a");
  if (f)
  {
    /* Log entry with control-flow relevant parameters and brief session state. */
    fprintf (f,
             "post_iterator probe: key='%s', kind=%d, filename='%s', content_type='%s', transfer_encoding='%s', off=%llu, size=%zu, session=%p\n",
             key ? key : "(null)",
             (int) kind,
             filename ? filename : "(null)",
             content_type ? content_type : "(null)",
             transfer_encoding ? transfer_encoding : "(null)",
             (unsigned long long) off,
             size,
             (void *) session);

    if (session)
    {
      fprintf (f,
               " session.sid='%s' value_1='%s' value_2='%s'\n",
               session->sid ? session->sid : "(null)",
               session->value_1 ? session->value_1 : "(null)",
               session->value_2 ? session->value_2 : "(null)");
    }

    /* Explicitly note that the (void) kind; no-op was executed at function start. */
    fprintf (f, " note: executed no-op '(void) kind;' at function start\n");
    fclose (f);
  }
}

static enum MHD_Result
post_iterator (void *cls,
               enum MHD_ValueKind kind,
               const char *key,
               const char *filename,
               const char *content_type,
               const char *transfer_encoding,
               const char *data, uint64_t off, size_t size)
{
  struct Request *request = cls;
  struct Session *session = request->session;
  (void) kind;              /* Unused. Silent compiler warning. */
  (void) filename;          /* Unused. Silent compiler warning. */
  (void) content_type;      /* Unused. Silent compiler warning. */
  (void) transfer_encoding; /* Unused. Silent compiler warning. */

  /* Probe immediately after the no-op casts to capture the fact they were reached. */
  post_iterator_probe (session,
                       kind,
                       filename,
                       content_type,
                       transfer_encoding,
                       key,
                       data,
                       off,
                       size);

  if (0 == strcmp ("DONE", key))
  {
    fprintf (stdout,
             "Session `%s' submitted `%s', `%s'\n",
             session->sid,
             session->value_1,
             session->value_2);
    return MHD_YES;
  }
  if (0 == strcmp ("v1", key))
  {
    if (off >= sizeof(session->value_1) - 1)
      return MHD_YES; /* Discard extra data */
    if (size + off >= sizeof(session->value_1))
      size = (size_t) (sizeof (session->value_1) - off - 1); /* crop extra data */
    memcpy (&session->value_1[off],
            data,
            size);
    session->value_1[size + off] = '\0';
    return MHD_YES;
  }
  if (0 == strcmp ("v2", key))
  {
    if (off >= sizeof(session->value_2) - 1)
      return MHD_YES; /* Discard extra data */
    if (size + off >= sizeof(session->value_2))
      size = (size_t) (sizeof (session->value_2) - off - 1); /* crop extra data */
    memcpy (&session->value_2[off],
            data,
            size);
    session->value_2[size + off] = '\0';
    return MHD_YES;
  }
  fprintf (stderr,
           "Unsupported form value `%s'\n",
           key);
  return MHD_YES;
}
