/* Logging helpers inserted to capture control flow and state for debugging.
   The helpers append human-readable traces to /tmp/logs/99.txt.
   These helpers are intentionally lightweight and do not change the original
   logic of post_process_urlencoded. */

static void
pp_log_event (struct MHD_PostProcessor *pp,
              const char *label,
              const char *post_data,
              size_t poff,
              const char *start_key,
              const char *end_key,
              const char *start_value,
              const char *end_value,
              const char *last_escape)
{
  FILE *f = fopen("/tmp/logs/99.txt", "a");
  if (NULL == f)
    return;
  /* Basic state */
  fprintf (f, "[%s] state=%d poff=%zu buffer_pos=%zu buffer_size=%zu must_ikvi=%d must_unescape_key=%d value_offset=%zu\n",
           label,
           (int) pp->state,
           poff,
           pp->buffer_pos,
           pp->buffer_size,
           (int) pp->must_ikvi,
           (int) pp->must_unescape_key,
           pp->value_offset);
  /* Pointer information relative to post_data */
  fprintf (f, "  pointers: start_key=%p end_key=%p start_value=%p end_value=%p last_escape=%p\n",
           (const void *) start_key,
           (const void *) end_key,
           (const void *) start_value,
           (const void *) end_value,
           (const void *) last_escape);
  if (NULL != post_data)
  {
    if (NULL != start_key)
      fprintf (f, "    start_key_offset=%td\n", (ptrdiff_t) (start_key - post_data));
    if (NULL != end_key)
      fprintf (f, "    end_key_offset=%td\n", (ptrdiff_t) (end_key - post_data));
    if (NULL != start_value)
      fprintf (f, "    start_value_offset=%td\n", (ptrdiff_t) (start_value - post_data));
    if (NULL != end_value)
      fprintf (f, "    end_value_offset=%td\n", (ptrdiff_t) (end_value - post_data));
    if (NULL != last_escape)
      fprintf (f, "    last_escape_offset=%td\n", (ptrdiff_t) (last_escape - post_data));
    /* If both start and end value are present, show length */
    if ((NULL != start_value) && (NULL != end_value))
      fprintf (f, "    value_len=%td\n", (ptrdiff_t) (end_value - start_value));
  }
  fprintf (f, "  poff_char=(%c) (0x%02x)\n",
           ((poff < (size_t) -1) && post_data && poff < SIZE_MAX) ? post_data[poff] : '?',
           ((poff < (size_t) -1) && post_data && poff < SIZE_MAX) ? (unsigned char) post_data[poff] : 0);
  fclose (f);
}

static enum MHD_Result
post_process_urlencoded (struct MHD_PostProcessor *pp,
                         const char *post_data,
                         size_t post_data_len)
{
  char *kbuf = (char *) &pp[1];
  size_t poff;
  const char *start_key = NULL;
  const char *end_key = NULL;
  const char *start_value = NULL;
  const char *end_value = NULL;
  const char *last_escape = NULL;

  mhd_assert (PP_Callback != pp->state);

  /* Log entering the function with initial state */
  pp_log_event (pp, "enter", post_data, 0, start_key, end_key, start_value, end_value, last_escape);

  poff = 0;
  while ( ( (poff < post_data_len) ||
            (pp->state == PP_Callback) ) &&
          (pp->state != PP_Error) )
  {
    /* Log each loop iteration entry */
    pp_log_event (pp, "loop_top", post_data, poff, start_key, end_key, start_value, end_value, last_escape);

    switch (pp->state)
    {
    case PP_Error:
      /* clearly impossible as per while loop invariant */
      abort ();
      break; /* Unreachable */
    case PP_Init:
      /* initial phase */
      mhd_assert (NULL == start_key);
      mhd_assert (NULL == end_key);
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      switch (post_data[poff])
      {
      case '=':
        /* Case: (no key)'=' */
        /* Empty key with value */
        pp->state = PP_Error;
        /* Log error transition */
        pp_log_event (pp, "init_equal_set_error", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      case '&':
        /* Case: (no key)'&' */
        /* Empty key without value */
        poff++;
        /* Log skipping empty key */
        pp_log_event (pp, "init_ampersand_skip", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      case '\n':
      case '\r':
        /* Case: (no key)'\n' or (no key)'\r' */
        pp->state = PP_Done;
        poff++;
        break;
      default:
        /* normal character, key start, advance! */
        pp->state = PP_ProcessKey;
        start_key = &post_data[poff];
        pp->must_ikvi = true;
        poff++;
        continue;
      }
      break; /* end PP_Init */
    case PP_ProcessKey:
      /* key phase */
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      mhd_assert (NULL != start_key || 0 == poff);
      mhd_assert (0 != poff || NULL == start_key);
      mhd_assert (NULL == end_key);
      switch (post_data[poff])
      {
      case '=':
        /* Case: 'key=' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_ProcessValue;
        break;
      case '&':
        /* Case: 'key&' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_Callback;
        break;
      case '\n':
      case '\r':
        /* Case: 'key\n' or 'key\r' */
        if (0 != poff)
          end_key = &post_data[poff];
        /* No advance here, 'PP_Done' will be selected by next 'PP_Init' phase */
        pp->state = PP_Callback;
        break;
      default:
        /* normal character, advance! */
        if (0 == poff)
          start_key = post_data;
        poff++;
        break;
      }
      mhd_assert (NULL == end_key || NULL != start_key);
      break; /* end PP_ProcessKey */
    case PP_ProcessValue:
      if (NULL == start_value)
        start_value = &post_data[poff];
      switch (post_data[poff])
      {
      case '=':
        /* case 'key==' */
        pp->state = PP_Error;
        /* Log unexpected double '=' in value */
        pp_log_event (pp, "value_double_equal_error", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      case '&':
        /* case 'value&' */
        end_value = &post_data[poff];
        poff++;
        /* Log encountering '&' finishing a value */
        pp_log_event (pp, "value_ampersand_end", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback;
        }
        else
        {
          pp->buffer_pos = 0;
          pp->value_offset = 0;
          pp->state = PP_Init;
          start_value = NULL;
          end_value = NULL;
        }
        continue;
      case '\n':
      case '\r':
        /* Case: 'value\n' or 'value\r' */
        end_value = &post_data[poff];
        if (pp->must_ikvi ||
            (start_value != end_value) )
          pp->state = PP_Callback; /* No poff advance here to set PP_Done in the next iteration */
        else
        {
          poff++;
          pp->state = PP_Done;
        }
        break;
      case '%':
        last_escape = &post_data[poff];
        poff++;
        /* Log start of percent-escape sequence */
        pp_log_event (pp, "value_percent_escape", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
        break;
      case '0':
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        /* character, may be part of escaping */
        poff++;
        continue;
      default:
        /* normal character, no more escaping! */
        last_escape = NULL;
        poff++;
        continue;
      }
      break; /* end PP_ProcessValue */
    case PP_Done:
      switch (post_data[poff])
      {
      case '\n':
      case '\r':
        poff++;
        continue;
      }
      /* unexpected data at the end, fail! */
      pp->state = PP_Error;
      /* Log unexpected trailing data */
      pp_log_event (pp, "done_unexpected_data", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
      break;
    case PP_Callback:
      mhd_assert ((NULL != end_key) || (NULL == start_key));
      if (1)
      {
        const size_t key_len = (size_t) (end_key - start_key);
        mhd_assert (end_key >= start_key);
        if (0 != key_len)
        {
          if ( (pp->buffer_pos + key_len >= pp->buffer_size) ||
               (pp->buffer_pos + key_len < pp->buffer_pos) )
          {
            /* key too long, cannot parse! */
            pp->state = PP_Error;
            continue;
          }
          /* compute key, if we have not already */
          memcpy (&kbuf[pp->buffer_pos],
                  start_key,
                  key_len);
          pp->buffer_pos += key_len;
          start_key = NULL;
          end_key = NULL;
          pp->must_unescape_key = true;
        }
      }
#ifdef _DEBUG
      else
        mhd_assert (0 != pp->buffer_pos);
#endif /* _DEBUG */
      if (pp->must_unescape_key)
      {
        kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
        MHD_unescape_plus (kbuf);
        MHD_http_unescape (kbuf);
        pp->must_unescape_key = false;
      }
      /* Log just before calling process_value from PP_Callback */
      pp_log_event (pp, "about_to_call_process_value_callback", post_data, poff, start_key, end_key, start_value, end_value, last_escape);

      process_value (pp,
                     start_value,
                     end_value,
                     NULL);

      /* Log immediately after the call so we have evidence of the call site and the pointers used */
      pp_log_event (pp, "after_call_process_value_callback", post_data, poff, start_key, end_key, start_value, end_value, last_escape);

      if (PP_Error == pp->state)
        continue;
      pp->value_offset = 0;
      start_value = NULL;
      end_value = NULL;
      pp->buffer_pos = 0;
      pp->state = PP_Init;
      break;
    case PP_NextBoundary:
    case PP_ProcessEntryHeaders:
    case PP_PerformCheckMultipart:
    case PP_ProcessValueToBoundary:
    case PP_PerformCleanup:
    case PP_Nested_Init:
    case PP_Nested_PerformMarking:
    case PP_Nested_ProcessEntryHeaders:
    case PP_Nested_ProcessValueToBoundary:
    case PP_Nested_PerformCleanup:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
    mhd_assert ((end_key == NULL) || (start_key != NULL));
    mhd_assert ((end_value == NULL) || (start_value != NULL));
  }

  mhd_assert (PP_Callback != pp->state);

  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    pp_log_event (pp, "exit_error", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
    return MHD_NO;
  }

  /* save remaining data for next iteration */
  if (NULL != start_key)
  {
    size_t key_len;
    mhd_assert ((PP_ProcessKey == pp->state) || (NULL != end_key));
    if (NULL == end_key)
      end_key = &post_data[poff];
    mhd_assert (end_key >= start_key);
    key_len = (size_t) (end_key - start_key);
    mhd_assert (0 != key_len); /* it must be always non-zero here */
    if (pp->buffer_pos + key_len >= pp->buffer_size)
    {
      pp->state = PP_Error;
      pp_log_event (pp, "exit_error_key_too_long", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
      return MHD_NO;
    }
    memcpy (&kbuf[pp->buffer_pos],
            start_key,
            key_len);
    pp->buffer_pos += key_len;
    pp->must_unescape_key = true;
    start_key = NULL;
    end_key = NULL;
  }
  if ( (NULL != start_value) &&
       (PP_ProcessValue == pp->state) )
  {
    /* compute key, if we have not already */
    if (pp->must_unescape_key)
    {
      kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
      MHD_unescape_plus (kbuf);
      MHD_http_unescape (kbuf);
      pp->must_unescape_key = false;
    }
    if (NULL == end_value)
      end_value = &post_data[poff];
    if ( (NULL != last_escape) &&
         (2 < (end_value - last_escape)) )
      last_escape = NULL;

    /* Log just before calling process_value for the final buffered value */
    pp_log_event (pp, "about_to_call_process_value_final", post_data, poff, start_key, end_key, start_value, end_value, last_escape);

    process_value (pp,
                   start_value,
                   end_value,
                   last_escape);

    /* Log immediately after the call to capture resulting state */
    pp_log_event (pp, "after_call_process_value_final", post_data, poff, start_key, end_key, start_value, end_value, last_escape);

    pp->must_ikvi = false;
  }
  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    pp_log_event (pp, "exit_error_postprocess", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
    return MHD_NO;
  }

  pp_log_event (pp, "exit_ok", post_data, poff, start_key, end_key, start_value, end_value, last_escape);
  return MHD_YES;
}
