#include <stdio.h>
#include <string.h>

/* Probe logger used to record control flow and state for send_bad_request.
   Writes append-only logs to /tmp/logs/498.txt. */
static void
send_bad_request_log (const char *event, struct MHD_Connection *con, struct MHD_Response *res, enum MHD_Result ret)
{
  FILE *f = fopen ("/tmp/logs/498.txt", "a");
  if (f != NULL)
    {
      /* Log pointer values, enum result (as integer) and length of BAD_REQUEST_PAGE
         to help reproduce and provide evidence for the reported scenario. */
      (void) fprintf (f, "send_bad_request: event=%s, con=%p, res=%p, ret=%d, BAD_REQUEST_PAGE_len=%zu\n",
                     event,
                     (void *) con,
                     (void *) res,
                     (int) ret,
                     strlen (BAD_REQUEST_PAGE));
      (void) fclose (f);
    }
}

static enum MHD_Result
send_bad_request (struct MHD_Connection *con)
{
  struct MHD_Response *res;
  enum MHD_Result ret;

  /* Log entry to the function */
  send_bad_request_log ("entry", con, NULL, 0);

  res =
    MHD_create_response_from_buffer_static (strlen (BAD_REQUEST_PAGE),
                                            (const void *) BAD_REQUEST_PAGE);

  /* Log after creating response object */
  send_bad_request_log ("response_created", con, res, 0);

  ret = MHD_queue_response (con, MHD_HTTP_BAD_REQUEST, res);

  /* Log after queuing response (ret may contain result code) */
  send_bad_request_log ("response_queued", con, res, ret);

  MHD_destroy_response (res);

  /* Log after destroying response and before return */
  send_bad_request_log ("response_destroyed", con, NULL, ret);

  return ret;
}
