#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logging helper: append logs to /tmp/logs/641.txt. */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/641.txt", "a");
  if (!f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

static ssize_t
send_iov_nontls (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
  ssize_t res;
  size_t items_to_send;
#ifdef HAVE_SENDMSG
  struct msghdr msg;
#elif defined(MHD_WINSOCK_SOCKETS)
  DWORD bytes_sent;
  DWORD cnt_w;
#endif /* MHD_WINSOCK_SOCKETS */

  mhd_assert (0 == (connection->daemon->options & MHD_USE_TLS));

  /* Log entry state */
  mhd_probe_log ("enter send_iov_nontls: connection=%p socket_fd=%ld state=%d daemon_options=0x%08x r_iov=%p r_iov->cnt=%zu r_iov->sent=%zu push_data=%d",
                 (void *) connection,
                 (long) connection->socket_fd,
                 (int) connection->state,
                 (unsigned) connection->daemon->options,
                 (void *) r_iov,
                 (size_t) r_iov->cnt,
                 (size_t) r_iov->sent,
                 (int) push_data);

  if ( (MHD_INVALID_SOCKET == connection->socket_fd) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_probe_log ("early return: invalid socket or closed connection");
    return MHD_ERR_NOTCONN_;
  }

  items_to_send = r_iov->cnt - r_iov->sent;
#ifdef _MHD_IOV_MAX
  if (_MHD_IOV_MAX < items_to_send)
  {
    mhd_assert (0 < _MHD_IOV_MAX);
    if (0 == _MHD_IOV_MAX)
    {
      mhd_probe_log ("_MHD_IOV_MAX == 0: returning MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_; /* Should never happen */
    }
    mhd_probe_log ("_MHD_IOV_MAX (%d) < items_to_send (%zu): truncating to %d and disabling push_data",
                   (int) _MHD_IOV_MAX, items_to_send, (int) _MHD_IOV_MAX);
    items_to_send = _MHD_IOV_MAX;
    push_data = false; /* Incomplete response */
  }
#endif /* _MHD_IOV_MAX */
#ifdef HAVE_SENDMSG
  mhd_probe_log ("branch chosen at compile-time: HAVE_SENDMSG");
  memset (&msg, 0, sizeof(struct msghdr));
  msg.msg_iov = r_iov->iov + r_iov->sent;
  msg.msg_iovlen = items_to_send;

  mhd_probe_log ("prepared msghdr: msg_iovlen=%zu r_iov->sent=%zu items_to_send=%zu push_data=%d",
                 (size_t) msg.msg_iovlen, (size_t) r_iov->sent, items_to_send, (int) push_data);
  pre_send_setopt (connection, true, push_data);
#ifdef MHD_USE_MSG_MORE
  res = sendmsg (connection->socket_fd, &msg,
                 MSG_NOSIGNAL_OR_ZERO | (push_data ? 0 : MSG_MORE));
#else  /* ! MHD_USE_MSG_MORE */
  res = sendmsg (connection->socket_fd, &msg, MSG_NOSIGNAL_OR_ZERO);
#endif /* ! MHD_USE_MSG_MORE */
  mhd_probe_log ("sendmsg returned: res=%ld", (long) res);
#elif defined(HAVE_WRITEV)
  mhd_probe_log ("branch chosen at compile-time: HAVE_WRITEV");
  mhd_probe_log ("writev params: socket_fd=%ld start_iov=%zu items_to_send=%zu push_data=%d",
                 (long) connection->socket_fd, (size_t) r_iov->sent, items_to_send, (int) push_data);
  pre_send_setopt (connection, true, push_data);
  res = writev (connection->socket_fd, r_iov->iov + r_iov->sent,
                items_to_send);
  mhd_probe_log ("writev returned: res=%ld", (long) res);
#elif defined(MHD_WINSOCK_SOCKETS)
  mhd_probe_log ("branch chosen at compile-time: MHD_WINSOCK_SOCKETS");
#ifdef _WIN64
  if (items_to_send > UINT32_MAX)
  {
    cnt_w = UINT32_MAX;
    push_data = false; /* Incomplete response */
  }
  else
    cnt_w = (DWORD) items_to_send;
#else  /* ! _WIN64 */
  cnt_w = (DWORD) items_to_send;
#endif /* ! _WIN64 */
  mhd_probe_log ("WSASend params: cnt_w=%u push_data=%d r_iov->sent=%zu",
                 (unsigned) cnt_w, (int) push_data, (size_t) r_iov->sent);
  pre_send_setopt (connection, true, push_data);
  if (0 == WSASend (connection->socket_fd,
                    (LPWSABUF) (r_iov->iov + r_iov->sent),
                    cnt_w,
                    &bytes_sent, 0, NULL, NULL))
    res = (ssize_t) bytes_sent;
  else
    res = -1;
  mhd_probe_log ("WSASend returned: res=%ld bytes_sent=%u", (long) res, (unsigned) bytes_sent);
#else /* !HAVE_SENDMSG && !HAVE_WRITEV && !MHD_WINSOCK_SOCKETS */
#error No vector-send function available
#endif

  if (0 > res)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_probe_log ("send error: res=%ld err=%d", (long) res, err);

    if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
    {
#ifdef EPOLL_SUPPORT
      /* EAGAIN --- no longer write-ready */
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      mhd_probe_log ("error path: EAGAIN -> return MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_probe_log ("error path: EINTR -> return MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
    {
      mhd_probe_log ("error path: REMOTE_DISCONNECT -> return MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
    {
      mhd_probe_log ("error path: EPIPE -> return MHD_ERR_PIPE_");
      return MHD_ERR_PIPE_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
    {
      mhd_probe_log ("error path: EOPNOTSUPP -> return MHD_ERR_OPNOTSUPP_");
      return MHD_ERR_OPNOTSUPP_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
    {
      mhd_probe_log ("error path: ENOTCONN -> return MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
    {
      mhd_probe_log ("error path: EINVAL -> return MHD_ERR_INVAL_");
      return MHD_ERR_INVAL_;
    }
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
    {
      mhd_probe_log ("error path: LOW_RESOURCES -> return MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
    {
      mhd_probe_log ("error path: EBADF -> return MHD_ERR_BADF_");
      return MHD_ERR_BADF_;
    }
    /* Treat any other error as a hard error. */
    mhd_probe_log ("error path: other -> return MHD_ERR_NOTCONN_");
    return MHD_ERR_NOTCONN_;
  }

  /* Some data has been sent */
  if (1)
  {
    size_t track_sent = (size_t) res;
    mhd_probe_log ("adjusting tracking: initial track_sent=%zu r_iov->sent=%zu r_iov->cnt=%zu",
                   track_sent, (size_t) r_iov->sent, (size_t) r_iov->cnt);
    /* Adjust the internal tracking information for the iovec to
     * take this last send into account. */
    while ((0 != track_sent) && (r_iov->iov[r_iov->sent].iov_len <= track_sent))
    {
      track_sent -= r_iov->iov[r_iov->sent].iov_len;
      r_iov->sent++; /* The iov element has been completely sent */
      mhd_assert ((r_iov->cnt > r_iov->sent) || (0 == track_sent));
      mhd_probe_log ("consumed iov element, new track_sent=%zu r_iov->sent=%zu",
                     track_sent, (size_t) r_iov->sent);
    }

    if (r_iov->cnt == r_iov->sent)
    {
      post_send_setopt (connection, true, push_data);
      mhd_probe_log ("all iov sent: r_iov->cnt == r_iov->sent (%zu)", (size_t) r_iov->sent);
    }
    else
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      if (0 != track_sent)
      {
        mhd_assert (r_iov->cnt > r_iov->sent);
        /* The last iov element has been partially sent */
        r_iov->iov[r_iov->sent].iov_base =
          (void *) ((uint8_t *) r_iov->iov[r_iov->sent].iov_base + track_sent);
        r_iov->iov[r_iov->sent].iov_len -= (MHD_iov_size_) track_sent;
        mhd_probe_log ("partial send: remaining in current iov new iov_base=%p iov_len=%zu",
                       r_iov->iov[r_iov->sent].iov_base,
                       (size_t) r_iov->iov[r_iov->sent].iov_len);
      }
    }
  }

  mhd_probe_log ("exit send_iov_nontls: res=%ld r_iov->sent=%zu",
                 (long) res, (size_t) r_iov->sent);

  return res;
}
