static void
log_probe (const char *fmt, ...)
{
  va_list ap;
  FILE *f = fopen("/tmp/logs/715.txt", "a");
  if (NULL == f)
    return;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  fflush (f);
  va_end (ap);
  fclose (f);
}

