static void
try_ready_log_probe_response_state (struct MHD_Connection *connection,
                                     struct MHD_Response *response,
                                     size_t copy_size,
                                     int overflow_flag,
                                     const char *stage)
{
  FILE *f = fopen("/tmp/logs/711.txt", "a");
  if (NULL == f)
    return;

  fprintf (f, "[try_ready_normal_body] stage=%s\n", stage);
  fprintf (f, " response=%p\n", (void *) response);
  fprintf (f, " response->data_iov=%p\n", (void *) response->data_iov);
  fprintf (f, " response->data_iovcnt=%llu\n", (unsigned long long) response->data_iovcnt);
  fprintf (f, " response->total_size=%llu\n", (unsigned long long) response->total_size);
  fprintf (f, " response->data_start=%llu\n", (unsigned long long) response->data_start);
  fprintf (f, " response->data_size=%llu\n", (unsigned long long) response->data_size);
  fprintf (f, " response->data_buffer_size=%llu\n", (unsigned long long) response->data_buffer_size);
  fprintf (f, " connection=%p\n", (void *) connection);
  fprintf (f, " connection->rp.resp_iov.iov=%p\n", (void *) connection->rp.resp_iov.iov);
  fprintf (f, " computed copy_size=%llu\n", (unsigned long long) copy_size);
  fprintf (f, " multiplication_overflow_detected=%d\n", overflow_flag);
  fprintf (f, "---\n");
  fclose (f);
}

static enum MHD_Result
try_ready_normal_body (struct MHD_Connection *connection)
{
  ssize_t ret;
  struct MHD_Response *response;

  response = connection->rp.response;
  mhd_assert (connection->rp.props.send_reply_body);

  if ( (0 == response->total_size) ||
                     /* TODO: replace the next check with assert */
       (connection->rp.rsp_write_position == response->total_size) )
    return MHD_YES;  /* 0-byte response is always ready */
  if (NULL != response->data_iov)
  {
    size_t copy_size;

    if (NULL != connection->rp.resp_iov.iov)
      return MHD_YES;

    /* Probe: compute and log potential overflow condition without changing logic */
    {
      int overflow = 0;
      if (response->data_iovcnt > ((size_t) -1) / sizeof (MHD_iovec_))
        overflow = 1;

      copy_size = response->data_iovcnt * sizeof (MHD_iovec_);

      /* Log state before allocation */
      try_ready_log_probe_response_state (connection, response, copy_size, overflow,
                                          "before_alloc");
    }

    connection->rp.resp_iov.iov = MHD_connection_alloc_memory_ (connection,
                                                                copy_size);
    if (NULL == connection->rp.resp_iov.iov)
    {
      /* Log allocation failure */
      try_ready_log_probe_response_state (connection, response, copy_size, 0,
                                          "alloc_failed");

      MHD_mutex_unlock_chk_ (&response->mutex);
      /* not enough memory */
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection (out of memory)."));
      return MHD_NO;
    }

    /* Log after successful allocation, before memcpy */
    try_ready_log_probe_response_state (connection, response, copy_size, 0,
                                        "after_alloc_before_memcpy");

    memcpy (connection->rp.resp_iov.iov,
            response->data_iov,
            copy_size);

    /* Log after memcpy to capture pointers and sizes (evidence if data_iov was smaller) */
    try_ready_log_probe_response_state (connection, response, copy_size, 0,
                                        "after_memcpy");

    connection->rp.resp_iov.cnt = response->data_iovcnt;
    connection->rp.resp_iov.sent = 0;
    return MHD_YES;
  }
  if (NULL == response->crc)
    return MHD_YES;
  if ( (response->data_start <=
        connection->rp.rsp_write_position) &&
       (response->data_size + response->data_start >
        connection->rp.rsp_write_position) )
    return MHD_YES; /* response already ready */
#if defined(_MHD_HAVE_SENDFILE)
  if (MHD_resp_sender_sendfile == connection->rp.resp_sender)
  {
    /* will use sendfile, no need to bother response crc */
    return MHD_YES;
  }
#endif /* _MHD_HAVE_SENDFILE */

  ret = response->crc (response->crc_cls,
                       connection->rp.rsp_write_position,
                       (char *) response->data,
                       (size_t) MHD_MIN ((uint64_t) response->data_buffer_size,
                                         response->total_size
                                         - connection->rp.rsp_write_position));
  if (0 > ret)
  {
    /* either error or http 1.0 transfer, close socket! */
    /* TODO: do not update total size, check whether response
     * was really with unknown size */
    response->total_size = connection->rp.rsp_write_position;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    if (MHD_CONTENT_READER_END_OF_STREAM == ret)
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_COMPLETED_OK);
    else
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection (application reported " \
                                 "error generating data)."));
    return MHD_NO;
  }
  response->data_start = connection->rp.rsp_write_position;
  response->data_size = (size_t) ret;
  if (0 == ret)
  {
    connection->state = MHD_CONNECTION_NORMAL_BODY_UNREADY;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    return MHD_NO;
  }
  return MHD_YES;
}
