static void *consumer_thread(void *arg)
{
#include <stdio.h>

    /* Preserve original no-op that suppresses unused parameter warning */
    (void)arg;

    /* Probe: log that the '(void)arg;' statement executed and capture argument value */
    FILE *log = fopen("/tmp/logs/642.txt", "a");
    if (log != NULL) {
        fprintf(log, "consumer_thread: entered, (void)arg executed, arg=%p\n", arg);
        fprintf(log, "consumer_thread: attempting to acquire lock\n");
        fflush(log);
    }

    metal_mutex_acquire(&lock);
    if (log != NULL) {
        fprintf(log, "consumer_thread: lock acquired\n");
        fflush(log);
    }

    while (!counter) {
        if (log != NULL) {
            fprintf(log, "consumer_thread: counter==0, waiting on nempty_condv\n");
            fflush(log);
        }
        metal_condition_wait(&nempty_condv, &lock);
        if (log != NULL) {
            fprintf(log, "consumer_thread: woke from wait, counter=%d\n", counter);
            fflush(log);
        }
    }

    counter--;
    if (log != NULL) {
        fprintf(log, "consumer_thread: decremented counter to %d\n", counter);
        fflush(log);
    }

    metal_condition_signal(&nfull_condv);
    if (log != NULL) {
        fprintf(log, "consumer_thread: signaled nfull_condv\n");
        fflush(log);
    }

    metal_mutex_release(&lock);
    if (log != NULL) {
        fprintf(log, "consumer_thread: released lock, exiting\n");
        fclose(log);
    }

    return NULL;
}
