#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <unistd.h>
#include <string.h>
#include <sys/eventfd.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/720.txt */
static void probe_log(const char *tag, const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/720.txt", "a");
    if (!f) {
        /* If logging cannot be opened, avoid changing program flow */
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    fprintf(f, "%s: ", tag);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int irq(void)
{
    int rc = 0;
    char *err_msg = "";
    enum metal_log_level mll = metal_get_log_level();
    int i, tst_irq[2];

    probe_log("ENTER", "irq() start");
    probe_log("STATE", "saved log level mll=%d", (int)mll);

    /* Do not show LOG_ERROR or LOG_DEBUG for expected fail case */
    metal_set_log_level(METAL_LOG_CRITICAL);
    probe_log("ACTION", "metal_set_log_level(METAL_LOG_CRITICAL) called");

    for (i = 0; i < 2; i++) {
        /* we only want to test the lib API, so create 'virtual' IRQs */
        tst_irq[i] = eventfd(0, 0);
        probe_log("EVENTFD", "eventfd() -> tst_irq[%d]=%d (errno=%d, strerror=%s)",
                  i, tst_irq[i], errno, strerror(errno));
        probe_log("CALL", "about to call metal_log(METAL_LOG_DEBUG, \"%s: irq %d associated with fd %d\\n\", __func__, %d, %d)",
                  __func__, i, tst_irq[i], i, tst_irq[i]);
        metal_log(METAL_LOG_DEBUG, "%s: irq %d associated with fd %d\n",
                  __func__, i, tst_irq[i]);
        probe_log("CALL_RET", "metal_log returned (no rc available) for debug message");
    }

    probe_log("ACTION", "registering irq 0 (fd=%d)", tst_irq[0]);
    rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
    probe_log("RESULT", "metal_irq_register(fd=%d) rc=%d", tst_irq[0], rc);
    if (rc) {
        err_msg = "register irq 0 fail drv_id 1\n";
        probe_log("ERROR", "%s", err_msg);
        goto out;
    }

    probe_log("ACTION", "registering irq 1 (fd=%d)", tst_irq[1]);
    rc = metal_irq_register(tst_irq[1], irq_handler, (void *)1);
    probe_log("RESULT", "metal_irq_register(fd=%d) rc=%d", tst_irq[1], rc);
    if (rc) {
        err_msg = "register irq 1 fail drv_id 1\n";
        probe_log("ERROR", "%s", err_msg);
        goto out;
    }

    probe_log("ACTION", "unregistering irq 0 (fd=%d)", tst_irq[0]);
    metal_irq_unregister(tst_irq[0]);
    probe_log("ACTION", "registering irq 0 again (fd=%d)", tst_irq[0]);
    rc = metal_irq_register(tst_irq[0], irq_handler, (void *)1);
    probe_log("RESULT", "metal_irq_register(after unregister) fd=%d rc=%d", tst_irq[0], rc);
    if (rc) {
        err_msg = "register irq 0 after unregistering failed\n";
        probe_log("ERROR", "%s", err_msg);
        goto out;
    }
    probe_log("ACTION", "unregistering irq 0 and irq 1 (fd=%d, %d)", tst_irq[0], tst_irq[1]);
    metal_irq_unregister(tst_irq[0]);
    metal_irq_unregister(tst_irq[1]);

out:
    for (i = 0; i < 2; i++) {
        probe_log("CLOSE", "closing tst_irq[%d]=%d", i, tst_irq[i]);
        close(tst_irq[i]);
    }
    metal_set_log_level(mll);
    probe_log("STATE", "restored log level to mll=%d", (int)mll);
    if ((err_msg[0] != '\0') && (!rc))
        rc = -EINVAL;
    probe_log("FINAL", "err_msg=%s rc=%d", err_msg, rc);
    if (rc) {
        probe_log("CALL", "about to call metal_log(METAL_LOG_ERROR, \"%s\")", err_msg);
        metal_log(METAL_LOG_ERROR, "%s", err_msg);
        probe_log("CALL_RET", "metal_log(METAL_LOG_ERROR) returned (no rc available)");
    }
    probe_log("EXIT", "irq() returning rc=%d", rc);
    return rc;
}
