#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>

static void metal_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/29.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int metal_bus_find(const char *name, struct metal_bus **result)
{
    struct metal_list *node;
    struct metal_bus *bus;

    metal_log_probe("metal_bus_find entry: name=%p, result=%p", (const void *)name, (void *)result);

    metal_list_for_each(&_metal.common.bus_list, node) {
        bus = metal_container_of(node, struct metal_bus, node);
        metal_log_probe("iter: node=%p bus=%p bus->name=%p", (void *)node, (void *)bus, (const void *)bus->name);
        metal_log_probe("    name pointer=%p", (const void *)name);

        if (bus->name == NULL) {
            metal_log_probe("    bus->name is NULL");
        } else {
            metal_log_probe("    bus->name not NULL");
        }
        if (name == NULL) {
            metal_log_probe("    name is NULL");
        } else {
            metal_log_probe("    name not NULL");
        }

        metal_log_probe("    about to call strcmp(bus->name, name)");
        if (strcmp(bus->name, name) == 0 && result) {
            *result = bus;
            metal_log_probe("    match found: bus=%p name=%p => returning 0", (void *)bus, (const void *)name);
            return 0;
        }
        metal_log_probe("    strcmp did not match or result was NULL");
    }
    metal_log_probe("no bus found: returning -ENOENT");
    return -ENOENT;
}
