#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>

/* Probe helper: append a formatted message to the trace file. */
static void log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/72.txt", "a");
    if (!f) {
        return; /* best-effort logging; do not alter program logic on failure */
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int metal_generic_dev_open(struct metal_bus *bus, const char *dev_name,
                           struct metal_device **device)
{
    struct metal_list *node;
    struct metal_device *dev;

    /* Original suppress-unused-parameter expression is kept to preserve logic. */
    (void)bus;

    /* Log entry and the fact that 'bus' is intentionally ignored. Do not reference
       'bus' anywhere else so that the (void)bus statement remains present. */
    log_probe("ENTER: metal_generic_dev_open called; bus ignored by design; dev_name=\"%s\"", dev_name ? dev_name : "(null)");

    metal_list_for_each(&_metal.common.generic_device_list, node) {
        dev = metal_container_of(node, struct metal_device, node);
        /* Log each device encountered during the search. */
        log_probe("ITER: examining device name=\"%s\"", dev && dev->name ? dev->name : "(null)");
        if (strcmp(dev->name, dev_name) == 0) {
            *device = dev;
            log_probe("MATCH: found device \"%s\"; calling metal_generic_dev_sys_open", dev_name);
            int ret = metal_generic_dev_sys_open(dev);
            log_probe("CALL_RETURN: metal_generic_dev_sys_open returned %d", ret);
            return ret;
        }
    }

    log_probe("NOT_FOUND: device \"%s\" not found; returning -ENODEV", dev_name ? dev_name : "(null)");
    return -ENODEV;
}
