#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <errno.h>
#include <stdatomic.h>

/* Helper logging function: appends formatted messages to /tmp/logs/107.txt */
static void log_append(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/107.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int metal_io_block_read(struct metal_io_region *io, unsigned long offset,
           void *restrict dst, int len)
{
    unsigned char *ptr = metal_io_virt(io, offset);
    unsigned char *dest = dst;
    int retlen;

    /* Log function entry state */
    log_append("ENTER metal_io_block_read: io=%p offset=%lu dst=%p len=%d io->size=%lu ops.block_read=%p",
               (void *)io, offset, dst, len, (io ? io->size : 0UL),
               (io ? (void *)io->ops.block_read : NULL));

    if (!ptr) {
        log_append("PTR NULL: returning -ERANGE");
        return -ERANGE;
    }

    log_append("PTR OK: ptr=%p", (void *)ptr);

    if ((offset + len) > io->size) {
        int old_len = len;
        len = io->size - offset;
        log_append("CLAMP len: old_len=%d new_len=%d (io->size=%lu offset=%lu)",
                   old_len, len, io->size, offset);
    }

    retlen = len;
    log_append("After clamp: retlen=%d len=%d", retlen, len);

    if (io->ops.block_read) {
        log_append("Using ops.block_read: calling block_read(io=%p, offset=%lu dst=%p len=%d)",
                   (void *)io, offset, dst, len);
        retlen = (*io->ops.block_read)(
            io, offset, dst, memory_order_seq_cst, len);
        log_append("block_read returned retlen=%d", retlen);
    } else {
        log_append("No ops.block_read: entering direct copy path");
        atomic_thread_fence(memory_order_seq_cst);

        /* First unaligned bytes until both pointers are aligned to sizeof(int) */
        while ( len && (
            ((uintptr_t)dest % sizeof(int)) ||
            ((uintptr_t)ptr % sizeof(int)))) {
            log_append("BYTE-LOOP1 start: len=%d ptr=%p dest=%p byte=*ptr=0x%02x",
                       len, (void *)ptr, (void *)dest, (unsigned int)(*(const unsigned char *)ptr));
            *(unsigned char *)dest = *(const unsigned char *)ptr;
            dest++;
            ptr++;
            len--;
            log_append("BYTE-LOOP1 iter: decremented len -> %d ptr=%p dest=%p",
                       len, (void *)ptr, (void *)dest);
        }

        /* Aligned bulk copy using sizeof(int) */
        for (; len >= (int)sizeof(int); dest += sizeof(int),
                    ptr += sizeof(int),
                    len -= sizeof(int)) {
            log_append("INT-LOOP start: len=%d ptr=%p dest=%p copying 4 bytes (word)",
                       len, (void *)ptr, (void *)dest);
            *(unsigned int *)dest = *(const unsigned int *)ptr;
            log_append("INT-LOOP iter: len after -= sizeof(int) -> %d ptr=%p dest=%p",
                       len - (int)sizeof(int), (void *)(ptr + sizeof(int)), (void *)(dest + sizeof(int)));
            /* Note: the actual len variable is decremented by the for-loop control */
        }

        /* Remaining tail bytes */
        for (; len != 0; dest++, ptr++, len--) {
            log_append("BYTE-LOOP2 start: len=%d ptr=%p dest=%p byte=*ptr=0x%02x",
                       len, (void *)ptr, (void *)dest, (unsigned int)(*(const unsigned char *)ptr));
            *(unsigned char *)dest = *(const unsigned char *)ptr;
            log_append("BYTE-LOOP2 iter: decremented len -> %d ptr=%p dest=%p",
                       len - 1, (void *)(ptr + 1), (void *)(dest + 1));
            /* len is decremented by the for-loop control */
        }
    }

    log_append("EXIT metal_io_block_read: returning retlen=%d final_len_parameter_snapshot=%d", retlen, len);
    return retlen;
}
