#include <stdio.h>
#include <stdint.h>
#include <stdatomic.h>
#include <limits.h>

static void log_state(const char *event, struct metal_io_region *io,
                      unsigned long offset, unsigned char value,
                      int len, unsigned char *ptr, int retlen)
{
    FILE *f = fopen("/tmp/logs/117.txt", "a");
    if (!f)
        return;
    unsigned long iosz = 0UL;
    if (io)
        iosz = (unsigned long)io->size;
    fprintf(f, "%s: io=%p offset=%lu value=0x%02x len=%d retlen=%d ptr=%p io->size=%lu\n",
            event, (void *)io, offset, (unsigned int)value, len, retlen,
            (void *)ptr, iosz);
    fclose(f);
}

int metal_io_block_set(struct metal_io_region *io, unsigned long offset,
               unsigned char value, int len)
{
    unsigned char *ptr = metal_io_virt(io, offset);
    int retlen = len;

    /* Log entry state */
    log_state("entry", io, offset, value, len, ptr, -1);

    if (!ptr) {
        log_state("ptr_null_return", io, offset, value, len, ptr, -ERANGE);
        return -ERANGE;
    }
    if ((offset + len) > io->size) {
        int oldlen = len;
        len = io->size - offset;
        log_state("len_adjusted", io, offset, value, len, ptr, oldlen);
    }
    retlen = len;
    log_state("after_len_adjust", io, offset, value, len, ptr, retlen);
    if (io->ops.block_set) {
        log_state("using_block_set_op", io, offset, value, len, ptr, retlen);
        (*io->ops.block_set)(
            io, offset, value, memory_order_seq_cst, len);
    } else {
        unsigned int cint = value;
        unsigned int i;

        log_state("using_memset_like_path_before_cint", io, offset, value, len, ptr, retlen);
        for (i = 1; i < sizeof(int); i++)
            cint |= ((unsigned int)value << (CHAR_BIT * i));

        log_state("after_cint_built", io, offset, value, len, ptr, retlen);

        /* align to int */
        log_state("start_align_loop", io, offset, value, len, ptr, retlen);
        for (; len && ((uintptr_t)ptr % sizeof(int)); ptr++, len--) {
            *(unsigned char *)ptr = (unsigned char) value;
            log_state("iter_align_loop", io, offset, value, len, ptr, retlen);
        }

        /* fill words */
        log_state("start_word_loop", io, offset, value, len, ptr, retlen);
        for (; len >= (int)sizeof(int); ptr += sizeof(int),
                         len -= sizeof(int)) {
            *(unsigned int *)ptr = cint;
            log_state("iter_word_loop", io, offset, value, len, ptr, retlen);
        }

        /* remaining bytes */
        log_state("start_tail_loop", io, offset, value, len, ptr, retlen);
        for (; len != 0; ptr++, len--) {
            *(unsigned char *)ptr = (unsigned char) value;
            log_state("iter_tail_loop", io, offset, value, len, ptr, retlen);
        }
        log_state("after_loops", io, offset, value, len, ptr, retlen);
        atomic_thread_fence(memory_order_seq_cst);
    }
    log_state("exit", io, offset, value, len, ptr, retlen);
    return retlen;
}
