#include <stdio.h>
#include <stdint.h>
#include <errno.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <stdatomic.h>

int metal_io_block_write(struct metal_io_region *io, unsigned long offset,
           const void *restrict src, int len)
{
    FILE *log = NULL;
    /* attempt to ensure log directory exists; ignore errors */
    (void)mkdir("/tmp/logs", 0777);
    log = fopen("/tmp/logs/127.txt", "a");

    unsigned char *ptr = metal_io_virt(io, offset);
    const unsigned char *source = src;
    int retlen;

    if (log) {
        fprintf(log, "ENTRY: metal_io_block_write io=%p offset=%lu src=%p len=%d io->size=%lu ptr_initial=%p\n",
                (void *)io, offset, src, len, (unsigned long)(io ? io->size : 0), (void *)ptr);
        fflush(log);
    }

    if (!ptr) {
        if (log) {
            fprintf(log, "EARLY-RETURN: ptr is NULL -> returning -ERANGE\n");
            fclose(log);
        }
        return -ERANGE;
    }
    if ((offset + len) > io->size) {
        if (log) {
            fprintf(log, "ADJUST: offset+len (%lu) > io->size (%lu): len adjusted from %d to %lu\n",
                    (unsigned long)(offset + len), (unsigned long)io->size, len,
                    (unsigned long)(io->size - offset));
        }
        len = io->size - offset;
        if (log) fflush(log);
    }
    retlen = len;
    if (log) {
        fprintf(log, "AFTER-ADJUST: retlen=%d (len=%d) ptr=%p source=%p\n",
                retlen, len, (void *)ptr, (void *)source);
        fflush(log);
    }
    if (io->ops.block_write) {
        if (log) {
            fprintf(log, "CALL: using ops.block_write (io=%p offset=%lu src=%p len=%d)\n",
                    (void *)&io->ops, offset, src, len);
            fflush(log);
        }
        retlen = (*io->ops.block_write)(
            io, offset, src, memory_order_seq_cst, len);
        if (log) {
            fprintf(log, "RETURN-FROM-OPS: retlen=%d\n", retlen);
            fclose(log);
        }
    } else {
        if (log) {
            fprintf(log, "MANUAL-COPY: entering manual copy path, initial len=%d\n", len);
            fflush(log);
        }
        while ( len && (
            ((uintptr_t)ptr % sizeof(int)) ||
            ((uintptr_t)source % sizeof(int)))) {
            if (log) {
                fprintf(log, "BYTE-COPY-BEFORE: ptr=%p source=%p len=%d -> copying one byte\n",
                        (void *)ptr, (void *)source, len);
                fflush(log);
            }
            *(unsigned char *)ptr =
                *(const unsigned char *)source;
            ptr++;
            source++;
            len--; /* <-- this is the parameter-modification of interest */
            if (log) {
                fprintf(log, "BYTE-COPY-AFTER: new len=%d ptr=%p source=%p\n",
                        len, (void *)ptr, (void *)source);
                fflush(log);
            }
        }
        for (; len >= (int)sizeof(int); ptr += sizeof(int),
                    source += sizeof(int),
                    len -= sizeof(int)) {
            if (log) {
                fprintf(log, "INT-COPY-BEFORE: ptr=%p source=%p len=%d -> copying %zu bytes\n",
                        (void *)ptr, (void *)source, len, sizeof(int));
                fflush(log);
            }
            *(unsigned int *)ptr = *(const unsigned int *)source;
            if (log) {
                fprintf(log, "INT-COPY-AFTER: new len=%d ptr=%p source=%p\n",
                        len - (int)sizeof(int), (void *)(ptr + sizeof(int)), (void *)(source + sizeof(int)));
                fflush(log);
            }
        }
        for (; len != 0; ptr++, source++, len--) {
            if (log) {
                fprintf(log, "TAIL-BYTE-COPY-BEFORE: ptr=%p source=%p len=%d -> copying one byte\n",
                        (void *)ptr, (void *)source, len);
                fflush(log);
            }
            *(unsigned char *)ptr =
                *(const unsigned char *)source;
            if (log) {
                fprintf(log, "TAIL-BYTE-COPY-AFTER: new len=%d ptr=%p source=%p\n",
                        len - 1, (void *)(ptr + 1), (void *)(source + 1));
                fflush(log);
            }
        }
        atomic_thread_fence(memory_order_seq_cst);
        if (log) {
            fprintf(log, "MANUAL-COPY-DONE: final retlen=%d len=%d ptr=%p source=%p\n",
                    retlen, len, (void *)ptr, (void *)source);
            fclose(log);
        }
    }
    return retlen;
}
