#include <stdio.h>
#include <stdint.h>
#include <stdatomic.h>
#include <errno.h>

int metal_io_block_write(struct metal_io_region *io, unsigned long offset,
           const void *restrict src, int len)
{
    unsigned char *ptr = metal_io_virt(io, offset);
    const unsigned char *source = src;
    int retlen;

    /* Probe: log entry and initial state */
    {
        FILE *log = fopen("/tmp/logs/127.txt", "a");
        if (log) {
            fprintf(log, "ENTER metal_io_block_write: io=%p offset=%lu src=%p len=%d\n",
                    (void *)io, offset, src, len);
            fprintf(log, "  ptr from metal_io_virt = %p\n", (void *)ptr);
            fclose(log);
        }
    }

    if (!ptr) {
        FILE *log = fopen("/tmp/logs/127.txt", "a");
        if (log) {
            fprintf(log, "RETURN -ERANGE: ptr is NULL\n");
            fclose(log);
        }
        return -ERANGE;
    }

    if ((offset + len) > io->size) {
        FILE *log = fopen("/tmp/logs/127.txt", "a");
        if (log) {
            fprintf(log, "Adjusting len for bounds: old_len=%d io->size=%lu offset=%lu\n",
                    len, (unsigned long)io->size, offset);
            fclose(log);
        }
        len = io->size - offset;
        FILE *log2 = fopen("/tmp/logs/127.txt", "a");
        if (log2) {
            fprintf(log2, "  new_len=%d\n", len);
            fclose(log2);
        }
    }

    retlen = len;

    if (io->ops.block_write) {
        FILE *log = fopen("/tmp/logs/127.txt", "a");
        if (log) {
            fprintf(log, "Using ops.block_write: calling block_write(io=%p, offset=%lu, src=%p, len=%d)\n",
                    (void *)io, offset, src, len);
            fclose(log);
        }
        retlen = (*io->ops.block_write)(
            io, offset, src, memory_order_seq_cst, len);
        FILE *log2 = fopen("/tmp/logs/127.txt", "a");
        if (log2) {
            fprintf(log2, "block_write returned retlen=%d\n", retlen);
            fclose(log2);
        }
    } else {
        FILE *log = fopen("/tmp/logs/127.txt", "a");
        if (log) {
            fprintf(log, "Falling back to byte/word copy path: initial len=%d ptr=%p source=%p\n",
                    len, (void *)ptr, (const void *)source);
            fclose(log);
        }

        while ( len && (
            ((uintptr_t)ptr % sizeof(int)) ||
            ((uintptr_t)source % sizeof(int)))) {
            unsigned char val = *(const unsigned char *)source;

            /* Log each byte copy in the unaligned prologue */
            FILE *log2 = fopen("/tmp/logs/127.txt", "a");
            if (log2) {
                fprintf(log2, "unaligned byte copy: ptr=%p source=%p val=0x%02x len(before)=%d\n",
                        (void *)ptr, (const void *)source, (unsigned)val, len);
                fclose(log2);
            }

            *(unsigned char *)ptr = val;
            ptr++;
            source++;
            len--;
        }

        for (; len >= (int)sizeof(int); ptr += sizeof(int),
                    source += sizeof(int),
                    len -= sizeof(int)) {
            unsigned int val = *(const unsigned int *)source;

            /* Log each word copy */
            FILE *log2 = fopen("/tmp/logs/127.txt", "a");
            if (log2) {
                fprintf(log2, "word copy: ptr=%p source=%p val=0x%08x len(after)=%d\n",
                        (void *)ptr, (const void *)source, val, len - (int)sizeof(int));
                fclose(log2);
            }

            *(unsigned int *)ptr = val;
        }

        for (; len != 0; ptr++, source++, len--) {
            unsigned char val = *(const unsigned char *)source;

            /* Log each remaining byte copy */
            FILE *log2 = fopen("/tmp/logs/127.txt", "a");
            if (log2) {
                fprintf(log2, "tail byte copy: ptr=%p source=%p val=0x%02x len(before)=%d\n",
                        (void *)ptr, (const void *)source, (unsigned)val, len);
                fclose(log2);
            }

            *(unsigned char *)ptr = val;
        }
        atomic_thread_fence(memory_order_seq_cst);

        FILE *log3 = fopen("/tmp/logs/127.txt", "a");
        if (log3) {
            fprintf(log3, "Completed manual copy path: retlen=%d\n", retlen);
            fclose(log3);
        }
    }

    FILE *log_end = fopen("/tmp/logs/127.txt", "a");
    if (log_end) {
        fprintf(log_end, "EXIT metal_io_block_write: retlen=%d\n", retlen);
        fclose(log_end);
    }

    return retlen;
}
