#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append formatted messages to /tmp/logs/153.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/153.txt", "a");
    if (f == NULL) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int metal_irq_allocate(int irq_base, int irq_num)
{
    struct metal_list *node;
    struct metal_irq_controller *cntr;
    int irq_tocheck = irq_base, irq_end_tocheck;

    probe_log("metal_irq_allocate: entry irq_base=%d irq_num=%d", irq_base, irq_num);

    if (irq_num == 0) {
        probe_log("metal_irq_allocate: irq_num == 0 -> return METAL_IRQ_ANY");
        return METAL_IRQ_ANY;
    }
    if (irq_tocheck == METAL_IRQ_ANY) {
        probe_log("metal_irq_allocate: irq_tocheck was METAL_IRQ_ANY, set to 0");
        irq_tocheck = 0;
    }
    irq_end_tocheck = irq_tocheck + irq_num;
    probe_log("metal_irq_allocate: initial irq_tocheck=%d irq_end_tocheck=%d", irq_tocheck, irq_end_tocheck);

    metal_list_for_each(&irq_cntrs, node) {
        int cntr_irq_base, cntr_irq_end;

        cntr = metal_container_of(node,
                                  struct metal_irq_controller, node);
        /* Log controller pointer and its public fields used below */
        probe_log("metal_irq_allocate: visiting controller %p", (void *)cntr);
        probe_log("metal_irq_allocate: controller->irq_base=%d controller->irq_num=%d",
                  cntr->irq_base, cntr->irq_num);

        cntr_irq_base = cntr->irq_base;
        cntr_irq_end = cntr_irq_base + cntr->irq_num;
        probe_log("metal_irq_allocate: computed cntr_irq_base=%d cntr_irq_end=%d",
                  cntr_irq_base, cntr_irq_end);

        if (irq_tocheck < cntr_irq_end &&
            irq_end_tocheck > cntr_irq_base) {
            probe_log("metal_irq_allocate: overlap detected (irq_tocheck=%d irq_end_tocheck=%d) with controller range [%d,%d)",
                      irq_tocheck, irq_end_tocheck, cntr_irq_base, cntr_irq_end);
            if (irq_base != METAL_IRQ_ANY) {
                /* IRQ has been allocated */
                probe_log("metal_irq_allocate: requested irq_base not METAL_IRQ_ANY and overlap -> return METAL_IRQ_ANY");
                return METAL_IRQ_ANY;
            }
            irq_tocheck = cntr_irq_end;
            irq_end_tocheck = irq_tocheck + irq_num;
            probe_log("metal_irq_allocate: updated irq_tocheck=%d irq_end_tocheck=%d", irq_tocheck, irq_end_tocheck);
        } else {
            probe_log("metal_irq_allocate: no overlap with controller %p", (void *)cntr);
        }
    }
    probe_log("metal_irq_allocate: exit returning irq_tocheck=%d", irq_tocheck);
    return irq_tocheck;
}
