#include <stdio.h>

static int metal_irq_allocate(int irq_base, int irq_num)
{
    struct metal_list *node;
    struct metal_irq_controller *cntr;
    int irq_tocheck = irq_base, irq_end_tocheck;
    FILE *f = fopen("/tmp/logs/153.txt", "a");

    if (f) {
        fprintf(f, "enter metal_irq_allocate: irq_base=%d irq_num=%d\n", irq_base, irq_num);
    }

    if (irq_num == 0) {
        if (f) {
            fprintf(f, "irq_num == 0 -> return METAL_IRQ_ANY\n");
            fclose(f);
        }
        return METAL_IRQ_ANY;
    }

    if (irq_tocheck == METAL_IRQ_ANY) {
        if (f) {
            fprintf(f, "irq_tocheck == METAL_IRQ_ANY -> set irq_tocheck = 0\n");
        }
        irq_tocheck = 0;
    }

    irq_end_tocheck = irq_tocheck + irq_num;
    if (f) {
        fprintf(f, "after init: irq_tocheck=%d irq_end_tocheck=%d\n", irq_tocheck, irq_end_tocheck);
    }

    metal_list_for_each(&irq_cntrs, node) {
        int cntr_irq_base, cntr_irq_end;

        cntr = metal_container_of(node,
                                  struct metal_irq_controller, node);
        cntr_irq_base = cntr->irq_base;
        cntr_irq_end = cntr_irq_base + cntr->irq_num;

        if (f) {
            fprintf(f, "checking controller: cntr=%p node=%p cntr_irq_base=%d cntr_irq_num=%d cntr_irq_end=%d\n",
                    (void *)cntr, (void *)node, cntr_irq_base, cntr->irq_num, cntr_irq_end);
        }

        if (irq_tocheck < cntr_irq_end &&
            irq_end_tocheck > cntr_irq_base) {
            if (f) {
                fprintf(f, "overlap detected: irq_tocheck=%d irq_end_tocheck=%d cntr_irq_base=%d cntr_irq_end=%d\n",
                        irq_tocheck, irq_end_tocheck, cntr_irq_base, cntr_irq_end);
            }
            if (irq_base != METAL_IRQ_ANY) {
                if (f) {
                    fprintf(f, "requested irq_base != METAL_IRQ_ANY and overlap -> return METAL_IRQ_ANY\n");
                    fclose(f);
                }
                return METAL_IRQ_ANY;
            }
            irq_tocheck = cntr_irq_end;
            irq_end_tocheck = irq_tocheck + irq_num;
            if (f) {
                fprintf(f, "advance irq_tocheck -> %d irq_end_tocheck=%d\n", irq_tocheck, irq_end_tocheck);
            }
        }
    }

    if (f) {
        fprintf(f, "no overlap found -> return %d\n", irq_tocheck);
        fclose(f);
    }
    return irq_tocheck;
}
