#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append a formatted log line to /tmp/logs/153.txt */
static void probe_log(const char *fmt, ...)
{
    va_list ap;
    FILE *f = fopen("/tmp/logs/153.txt", "a");
    if (!f) {
        return;
    }
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int metal_irq_allocate(int irq_base, int irq_num)
{
    struct metal_list *node;
    struct metal_irq_controller *cntr;
    int irq_tocheck = irq_base, irq_end_tocheck;

    probe_log("ENTER metal_irq_allocate: irq_base=%d, irq_num=%d", irq_base, irq_num);

    if (irq_num == 0) {
        probe_log("EARLY RETURN: irq_num == 0 -> METAL_IRQ_ANY (%d)", METAL_IRQ_ANY);
        return METAL_IRQ_ANY;
    }
    if (irq_tocheck == METAL_IRQ_ANY) {
        probe_log("irq_tocheck was METAL_IRQ_ANY, reset to 0");
        irq_tocheck = 0;
    }
    irq_end_tocheck = irq_tocheck + irq_num;

    probe_log("BEFORE LOOP: irq_tocheck=%d, irq_end_tocheck=%d", irq_tocheck, irq_end_tocheck);

    metal_list_for_each(&irq_cntrs, node) {
        int cntr_irq_base, cntr_irq_end;

        /* Log the node pointer being traversed */
        probe_log("LOOP: node=%p", (void *)node);

        cntr = metal_container_of(node,
                      struct metal_irq_controller, node);

        probe_log("  Derived cntr=%p", (void *)cntr);

        cntr_irq_base = cntr->irq_base;
        cntr_irq_end = cntr_irq_base + cntr->irq_num;

        probe_log("  cntr_irq_base=%d, cntr_irq_end=%d", cntr_irq_base, cntr_irq_end);
        probe_log("  Comparing: irq_tocheck=%d < cntr_irq_end=%d && irq_end_tocheck=%d > cntr_irq_base=%d",
                  irq_tocheck, cntr_irq_end, irq_end_tocheck, cntr_irq_base);

        if (irq_tocheck < cntr_irq_end &&
            irq_end_tocheck > cntr_irq_base) {
            probe_log("  OVERLAP detected with controller at %p", (void *)cntr);
            if (irq_base != METAL_IRQ_ANY) {
                /* IRQ has been allocated */
                probe_log("  IRQ requested (irq_base=%d) is already allocated -> returning METAL_IRQ_ANY (%d)",
                          irq_base, METAL_IRQ_ANY);
                return METAL_IRQ_ANY;
            }
            irq_tocheck = cntr_irq_end;
            irq_end_tocheck = irq_tocheck + irq_num;
            probe_log("  Adjusting irq_tocheck to %d, irq_end_tocheck to %d", irq_tocheck, irq_end_tocheck);
        }
    }
    probe_log("EXIT metal_irq_allocate: returning irq_tocheck=%d", irq_tocheck);
    return irq_tocheck;
}
