#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <stdlib.h>
#include <string.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/335.txt. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/335.txt", "a");
    if (!f)
        return;

    time_t t = time(NULL);
    struct tm tm_buf;
    (void)localtime_r(&t, &tm_buf);
    char timebuf[64];
    (void)strftime(timebuf, sizeof(timebuf), "%Y-%m-%d %H:%M:%S", &tm_buf);

    fprintf(f, "[%s] ", timebuf);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

static int metal_linux_dev_open(struct metal_bus *bus,
                const char *dev_name,
                struct metal_device **device)
{
    struct linux_bus *lbus = to_linux_bus(bus);
    struct linux_device *ldev = NULL;
    struct linux_driver *ldrv;
    int error;

    probe_log("enter: bus=%p, dev_name=%s, device_out=%p", (void *)bus, dev_name ? dev_name : "(null)", (void *)device);

    ldev = malloc(sizeof(*ldev));
    probe_log("malloc initial ldev -> %p", (void *)ldev);
    if (!ldev) {
        probe_log("malloc failed: returning -ENOMEM");
        return -ENOMEM;
    }

    for_each_linux_driver(lbus, ldrv) {
        probe_log("loop: ldrv=%p, ldrv->sdrv=%p, ldrv->dev_open=%p", (void *)ldrv,
                  (void *)(ldrv ? ldrv->sdrv : NULL), (void *)(ldrv ? ldrv->dev_open : NULL));

        /* Check if we have a viable driver. */
        if (!ldrv->sdrv || !ldrv->dev_open) {
            probe_log("skipping driver %p because no sdrv or dev_open", (void *)ldrv);
            continue;
        }

        /* Allocate a linux device if we haven't already. */
        if (!ldev) {
            probe_log("ldev is NULL inside loop, attempting malloc");
            ldev = malloc(sizeof(*ldev));
            probe_log("malloc inside loop ldev -> %p", (void *)ldev);
        }
        if (!ldev) {
            probe_log("malloc inside loop failed: returning -ENOMEM");
            return -ENOMEM;
        }

        /* Reset device data. */
        probe_log("resetting ldev %p", (void *)ldev);
        memset(ldev, 0, sizeof(*ldev));
        strncpy(ldev->dev_name, dev_name, sizeof(ldev->dev_name) - 1);
        ldev->fd = -1;
        ldev->ldrv = ldrv;
        ldev->device.bus = bus;

        probe_log("calling dev_open: ldrv=%p, ldev=%p, dev_name=%s", (void *)ldrv, (void *)ldev,
                  ldev->dev_name);

        /* Try and open the device. */
        error = ldrv->dev_open(lbus, ldev);
        probe_log("dev_open returned %d for ldrv=%p, ldev=%p", error, (void *)ldrv, (void *)ldev);
        if (error) {
            probe_log("dev_open failed, calling dev_close for ldrv=%p, ldev=%p", (void *)ldrv, (void *)ldev);
            ldrv->dev_close(lbus, ldev);
            continue;
        }

        *device = &ldev->device;
        (*device)->name = ldev->dev_name;

        probe_log("success: device=%p name=%s, adding to bus list", (void *)*device, (*device)->name);
        metal_list_add_tail(&bus->devices, &(*device)->node);
        return 0;
    }

    probe_log("exited driver loop, ldev currently %p", (void *)ldev);
    if (ldev) {
        probe_log("freeing ldev %p before return -ENODEV", (void *)ldev);
        free(ldev);
    } else {
        probe_log("ldev is NULL at end (unexpected in reported scenario)");
    }

    probe_log("returning -ENODEV");
    return -ENODEV;
}
