#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <string.h>
#include <stdlib.h>

/* Simple probe logger: append human-readable logs to /tmp/logs/335.txt */
static void probe_log_printf(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/335.txt", "a");
    if (!f)
        return;
    va_list args;
    va_start(args, fmt);
    vfprintf(f, fmt, args);
    fprintf(f, "\n");
    va_end(args);
    fclose(f);
}

static int metal_linux_dev_open(struct metal_bus *bus,
                const char *dev_name,
                struct metal_device **device)
{
    struct linux_bus *lbus = to_linux_bus(bus);
    struct linux_device *ldev = NULL;
    struct linux_driver *ldrv;
    int error;

    probe_log_printf("metal_linux_dev_open: enter, bus=%p dev_name=%s", (void *)bus, dev_name ? dev_name : "(null)");

    ldev = malloc(sizeof(*ldev));
    probe_log_printf("initial malloc -> ldev=%p", (void *)ldev);
    if (!ldev) {
        probe_log_printf("initial malloc failed: returning -ENOMEM");
        return -ENOMEM;
    }

    for_each_linux_driver(lbus, ldrv) {
        probe_log_printf("loop start: ldrv=%p", (void *)ldrv);

        if (ldrv) {
            probe_log_printf("ldrv details: sdrv=%p dev_open=%p dev_close=%p",
                             (void *)ldrv->sdrv, (void *)ldrv->dev_open, (void *)ldrv->dev_close);
        } else {
            probe_log_printf("ldrv is NULL (unexpected)");
        }

        /* Check if we have a viable driver. */
        if (!ldrv->sdrv || !ldrv->dev_open) {
            probe_log_printf("skipping driver %p: sdrv=%p dev_open=%p",
                             (void *)ldrv, (void *)ldrv->sdrv, (void *)ldrv->dev_open);
            continue;
        }

        /* Allocate a linux device if we haven't already. */
        probe_log_printf("before inner alloc check: ldev=%p", (void *)ldev);
        if (!ldev)
            ldev = malloc(sizeof(*ldev));
        probe_log_printf("after inner alloc check: ldev=%p", (void *)ldev);
        if (!ldev) {
            probe_log_printf("inner malloc failed: returning -ENOMEM");
            return -ENOMEM;
        }

        /* Reset device data. */
        probe_log_printf("resetting ldev %p and copying name=%s", (void *)ldev, dev_name ? dev_name : "(null)");
        memset(ldev, 0, sizeof(*ldev));
        strncpy(ldev->dev_name, dev_name, sizeof(ldev->dev_name) - 1);
        ldev->fd = -1;
        ldev->ldrv = ldrv;
        ldev->device.bus = bus;

        /* Try and open the device. */
        probe_log_printf("calling dev_open for ldrv=%p ldev=%p", (void *)ldrv, (void *)ldev);
        error = ldrv->dev_open(lbus, ldev);
        probe_log_printf("dev_open returned %d for ldrv=%p", error, (void *)ldrv);
        if (error) {
            probe_log_printf("dev_open failed, calling dev_close for ldrv=%p ldev=%p", (void *)ldrv, (void *)ldev);
            ldrv->dev_close(lbus, ldev);
            continue;
        }

        *device = &ldev->device;
        (*device)->name = ldev->dev_name;

        probe_log_printf("device opened successfully: device=%p name=%s; adding to bus devices and returning 0",
                         (void *)(*device), (*device)->name ? (*device)->name : "(null)");

        metal_list_add_tail(&bus->devices, &(*device)->node);
        return 0;
    }

    if (ldev) {
        probe_log_printf("no driver opened the device: freeing ldev=%p and returning -ENODEV", (void *)ldev);
        free(ldev);
    } else {
        probe_log_printf("no ldev allocated at end (unexpected), returning -ENODEV");
    }

    return -ENODEV;
}
