static void probe_log_state(const char *label, int sched_ret)
{
    FILE *f = fopen("/tmp/logs/479.txt", "a");
    if (!f)
        return;

    /* Log provided label, the return value from sched_setscheduler and errno. */
    fprintf(f, "[probe] %s\n", label);
    fprintf(f, "[probe] sched_setscheduler returned: ret=%d, errno=%d\n",
            sched_ret, errno);

    /* Log the strerror results for both the (potentially invalid) ret and errno. */
    /* Note: calling strerror(sched_ret) reproduces the original (incorrect) behavior. */
    fprintf(f, "[probe] strerror(ret) -> %s\n", strerror(sched_ret));
    fprintf(f, "[probe] strerror(errno) -> %s\n", strerror(errno));

    fclose(f);
}

static void *metal_linux_irq_handling(void *args)
{
	struct sched_param param;
	uint64_t val;
	int ret;
	int i, j, pfds_total;
	struct pollfd *pfds;

	(void)args;

	pfds = (struct pollfd *)malloc(FD_SETSIZE * sizeof(struct pollfd));
	if (!pfds) {
		metal_log(METAL_LOG_ERROR,
			  "%s: failed to allocate irq fds mem.\n", __func__);
		/* Probe the allocation failure as well */
		{
			FILE *f = fopen("/tmp/logs/479.txt", "a");
			if (f) {
				fprintf(f, "[probe] %s: malloc failed for pfds (FD_SETSIZE=%d)\n",
					__func__, FD_SETSIZE);
				fclose(f);
			}
		}
		return NULL;
	}

	param.sched_priority = sched_get_priority_max(SCHED_FIFO);
	/* Ignore the set scheduler error */
	ret = sched_setscheduler(0, SCHED_FIFO, &param);
	if (ret) {
		/* Probe: record the return value and errno and the string results. */
		probe_log_state("After sched_setscheduler", ret);

		/* Preserve original logging behavior (this still calls strerror(ret) as before) */
		metal_log(METAL_LOG_WARNING,
			  "%s: Failed to set scheduler: %s.\n", __func__,
			  strerror(ret));
	}

	while (1) {
		metal_mutex_acquire(&irq_lock);
		if (irq_handling_stop) {
			/* Killing this IRQ handling thread */
			metal_mutex_release(&irq_lock);
			break;
		}

		/* Get the fdset */
		memset(pfds, 0, MAX_IRQS * sizeof(struct pollfd));
		pfds[0].fd = irq_notify_fd;
		pfds[0].events = POLLIN;
		j = 1;
		metal_bitmap_for_each_set_bit(irqs_enabled, i,
					      linux_irq_cntr.irq_num) {
			pfds[j].fd = i;
			pfds[j].events = POLLIN;
			j++;
		}
		metal_mutex_release(&irq_lock);
		/* Wait for interrupt */
		ret = poll(pfds, j, -1);
		if (ret < 0) {
			/* Probe poll failure with errno info */
			{
				FILE *f = fopen("/tmp/logs/479.txt", "a");
				if (f) {
					fprintf(f, "[probe] %s: poll() failed: ret=%d, errno=%d, strerror(errno)=%s\n",
						__func__, ret, errno, strerror(errno));
					fclose(f);
				}
			}
			metal_log(METAL_LOG_ERROR, "%s: poll() failed: %s.\n",
				  __func__, strerror(errno));
			break;
		}
		/* Waken up from interrupt */
		pfds_total = j;
		for (i = 0; i < pfds_total; i++) {
			if ((pfds[i].fd == irq_notify_fd) &&
			    (pfds[i].revents & (POLLIN | POLLRDNORM))) {
				/* IRQ registration change notification */
				if (read(pfds[i].fd,
					 (void *)&val, sizeof(uint64_t)) < 0)
					metal_log(METAL_LOG_ERROR,
						"%s, read irq fd %d failed\n",
						__func__, pfds[i].fd);
			} else if ((pfds[i].revents & (POLLIN | POLLRDNORM))) {
				struct metal_device *dev = NULL;
				int irq_handled = 0;
				int fd;

				fd = pfds[i].fd;
				dev = irqs_devs[fd];
				metal_mutex_acquire(&irq_lock);
				if (metal_irq_handle(&irqs[fd], fd)
				    == METAL_IRQ_HANDLED)
					irq_handled = 1;
				if (irq_handled) {
					if (dev && dev->bus->ops.dev_irq_ack)
						dev->bus->ops.dev_irq_ack(
							dev->bus, dev, fd);
				}
				metal_mutex_release(&irq_lock);
			} else if (pfds[i].revents) {
				metal_log(METAL_LOG_DEBUG,
					  "%s: poll unexpected. fd %d: %d\n",
					  __func__,
					  pfds[i].fd, pfds[i].revents);
			}
		}
	}
	free(pfds);
	return NULL;
}
