#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>
#include <stdarg.h>
#include <errno.h>
#include <string.h>
#include <unistd.h>
#include <sys/types.h>

/* Probe helper: append formatted log lines to /tmp/logs/493.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/493.txt", "a");
    if (f == NULL) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

/* Modified function with probes that record control flow and state. */
static int metal_linux_irq_notify(void)
{
    uint64_t val = 1;
    int ret;

    /* Log entry and the initial state: fd, address of val, and its value. */
    probe_log("ENTER %s: irq_notify_fd=%d val_addr=%p val_initial=%" PRIu64,
              __func__, irq_notify_fd, (void *)&val, val);

    ret = write(irq_notify_fd, &val, sizeof(val));

    if (ret < 0) {
        int saved_errno = errno; /* capture errno immediately */
        probe_log("WRITE FAILED %s: irq_notify_fd=%d val_addr=%p val=%" PRIu64
                  " size=%zu ret=%d errno=%d (%s)",
                  __func__, irq_notify_fd, (void *)&val, val,
                  sizeof(val), ret, saved_errno, strerror(saved_errno));
        metal_log(METAL_LOG_ERROR, "%s failed\n", __func__);
    } else {
        probe_log("WRITE OK %s: irq_notify_fd=%d wrote=%d expected=%zu val_addr=%p val=%" PRIu64,
                  __func__, irq_notify_fd, ret, sizeof(val), (void *)&val, val);
    }

    return ret;
}
