#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>

/* Simple logging helper that appends structured logs to /tmp/logs/363.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/363.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int metal_linux_probe_driver(struct linux_bus *lbus,
                                    struct linux_driver *ldrv)
{
    char command[256];
    int ret;

    probe_log("ENTER metal_linux_probe_driver: lbus=%p ldrv=%p", (void *)lbus, (void *)ldrv);
    probe_log("state before: lbus->bus_name='%s' ldrv->drv_name='%s' ldrv->mod_name='%s' ldrv->sdrv=%p",
              (lbus && lbus->bus_name) ? lbus->bus_name : "(null)",
              (ldrv && ldrv->drv_name) ? ldrv->drv_name : "(null)",
              (ldrv && ldrv->mod_name) ? ldrv->mod_name : "(null)",
              (void *)(ldrv ? ldrv->sdrv : NULL));

    ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
    probe_log("after sysfs_open_driver (initial): ldrv->sdrv=%p", (void *)ldrv->sdrv);

    /* Try probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        ret = snprintf(command, sizeof(command),
                       "modprobe %s > /dev/null 2>&1", ldrv->mod_name);
        probe_log("snprintf returned %d, command='%s'", ret, command);
        if (ret >= (int)sizeof(command)) {
            probe_log("ERROR: snprintf overflow, returning -EOVERFLOW");
            return -EOVERFLOW;
        }
        ret = system(command);
        probe_log("system returned %d for command '%s'", ret, command);
        if (ret < 0) {
            metal_log(METAL_LOG_WARNING,
                      "%s: executing system command '%s' failed.\n",
                      __func__, command);
            probe_log("metal_log warning: executing system command failed: '%s'", command);
        }
        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
        probe_log("after sysfs_open_driver (after modprobe): ldrv->sdrv=%p", (void *)ldrv->sdrv);
    }

    /* Try sudo probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        ret = snprintf(command, sizeof(command),
                       "sudo modprobe %s > /dev/null 2>&1", ldrv->mod_name);
        probe_log("snprintf returned %d (sudo), command='%s'", ret, command);
        if (ret >= (int)sizeof(command)) {
            probe_log("ERROR: snprintf overflow (sudo), returning -EOVERFLOW");
            return -EOVERFLOW;
        }
        ret = system(command);
        probe_log("system returned %d for command '%s' (sudo)", ret, command);
        if (ret < 0) {
            metal_log(METAL_LOG_WARNING,
                      "%s: executing system command '%s' failed.\n",
                      __func__, command);
            probe_log("metal_log warning: executing sudo system command failed: '%s'", command);
        }
        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
        probe_log("after sysfs_open_driver (after sudo modprobe): ldrv->sdrv=%p", (void *)ldrv->sdrv);
    }

    probe_log("EXIT metal_linux_probe_driver: ldrv->sdrv=%p return=%d",
              (void *)ldrv->sdrv, ldrv->sdrv ? 0 : -ENODEV);

    /* If all else fails... */
    return ldrv->sdrv ? 0 : -ENODEV;
}
