#include <stdio.h>
#include <stdarg.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <unistd.h>

/* Probe logging helper: append textual traces to /tmp/logs/520.txt. */
static void probe_log(const char *fmt, ...)
{
    FILE *f;
    va_list ap;

    /* Try to create directory if it does not exist. Ignore errors. */
    (void)mkdir("/tmp/logs", 0755);

    f = fopen("/tmp/logs/520.txt", "a");
    if (!f)
        return;

    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fflush(f);
    fclose(f);
}

int metal_shmem_open(const char *name, size_t size,
             struct metal_io_region **result)
{
    struct metal_page_size *ps;
    int fd, error;

    probe_log("ENTER metal_shmem_open: name=%s size=%zu result=%p", name ? name : "(null)", size, (void *)result);

    error = metal_shmem_open_generic(name, size, result);
    probe_log("metal_shmem_open_generic returned %d", error);
    if (!error) {
        probe_log("generic path succeeded, returning %d", error);
        return error;
    }

    error = metal_open(name, 1);
    probe_log("metal_open(%s,1) returned %d", name ? name : "(null)", error);
    if (error < 0) {
        probe_log("metal_open failed (error=%d). About to call metal_log and return.", error);
        metal_log(METAL_LOG_ERROR, "Failed to open shmem file :%s\n", name);
        probe_log("metal_log invoked for error=%d", error);
        return error;
    }
    fd = error;
    probe_log("opened fd=%d", fd);

    /* Iterate through page sizes in decreasing order. */
    metal_for_each_page_size_down(ps) {
        probe_log("considering page_size=%zu (requested size=%zu)", ps->page_size, size);
        if (ps->page_size > 2 * size) {
            probe_log("skipping page_size=%zu because it's > 2*size", ps->page_size);
            continue;
        }
        error = metal_shmem_try_map(ps, fd, size, result);
        probe_log("metal_shmem_try_map returned %d for page_size=%zu", error, ps->page_size);
        if (!error) {
            probe_log("metal_shmem_try_map succeeded with page_size=%zu", ps->page_size);
            break;
        }
    }

    close(fd);
    probe_log("closed fd=%d, returning %d", fd, error);
    return error;
}
